css:
:host { display: block; margin: 1.5rem 0; font-family: var(--font-interface); }
.widget-card {
    background: var(--background-secondary);
    color: var(--text-normal);
    border-radius: 12px;
    padding: 1.5rem;
    box-shadow: var(--shadow-s);
    border: 1px solid var(--background-modifier-border);
    text-align: center;
}
.widget-title { margin: 0 0 1.5rem 0; font-size: 0.9rem; font-weight: 800; color: var(--interactive-accent); text-transform: uppercase; letter-spacing: 2px; }

.input-group { text-align: left; }
.input-group label { display: block; font-size: 0.65rem; font-weight: 700; color: var(--interactive-accent); margin-bottom: 4px; text-transform: uppercase; }
.input-group textarea {
    width: 100%;
    height: 80px;
    background: var(--background-primary);
    border: 1px solid var(--background-modifier-border);
    color: var(--text-normal);
    caret-color: var(--interactive-accent);
    border-radius: 4px;
    padding: 10px;
    font-size: 0.85rem;
    outline: none;
    resize: none;
    font-family: var(--font-monospace);
}
.input-group textarea:focus {
    border-color: var(--interactive-accent);
}

.separator { margin: 8px 0; font-size: 1.2rem; color: var(--text-muted); }

.actions { display: flex; gap: 8px; margin-top: 1rem; }
.actions button {
    flex: 1;
    background: var(--interactive-accent);
    color: var(--text-on-accent);
    border: none;
    padding: 8px;
    border-radius: 4px;
    font-weight: 600;
    font-size: 0.75rem;
    cursor: pointer;
    transition: opacity 0.2s;
}
.actions button:hover { opacity: 0.8; }
.actions .btn-clear {
    background: var(--background-modifier-error);
    color: white;
}

js:
const { root, saveState, getState } = api;

function encode() {
    const raw = root.getElementById('raw-text').value;
    try {
        const b64 = btoa(unescape(encodeURIComponent(raw)));
        root.getElementById('b64-text').value = b64;
    } catch(e) { 
        root.getElementById('b64-text').value = '';
    }
}

function decode() {
    const b64 = root.getElementById('b64-text').value;
    try {
        const raw = decodeURIComponent(escape(atob(b64)));
        root.getElementById('raw-text').value = raw;
    } catch(e) { 
        root.getElementById('raw-text').value = '';
    }
}

function save() {
    const raw = root.getElementById('raw-text').value;
    saveState(raw);
}

function clearFields() {
    root.getElementById('raw-text').value = '';
    root.getElementById('b64-text').value = '';
    saveState('');
}

function copy(id) {
    const el = root.getElementById(id);
    if (navigator.clipboard && navigator.clipboard.writeText) {
        navigator.clipboard.writeText(el.value);
    } else {
        el.select();
        document.execCommand('copy');
    }
}

async function init() {
    const saved = await getState();
    if (saved) {
        root.getElementById('raw-text').value = saved;
        const b64 = btoa(unescape(encodeURIComponent(saved)));
        root.getElementById('b64-text').value = b64;
    }
}

init();
