css:
:host { display: block; margin: 1.5rem 0; font-family: var(--font-interface); }
.widget-card {
    background: #f8fafc;
    color: #334155;
    border-radius: 20px;
    padding: 1.5rem;
    box-shadow: 0 10px 25px rgba(0,0,0,0.05);
    border: 1px solid #e2e8f0;
    text-align: center;
}
.widget-title { margin: 0 0 1.5rem 0; font-size: 0.9rem; font-weight: 800; color: #6366f1; text-transform: uppercase; letter-spacing: 2px; }

.input-group { text-align: left; }
.input-group label { display: block; font-size: 0.65rem; font-weight: 700; color: #6366f1; margin-bottom: 4px; text-transform: uppercase; }
.input-group textarea {
    width: 100%;
    height: 80px;
    background: white;
    border: 1px solid #e2e8f0;
    color: #334155;
    border-radius: 8px;
    padding: 10px;
    font-size: 0.85rem;
    outline: none;
    resize: none;
    font-family: var(--font-monospace);
}

.separator { margin: 8px 0; font-size: 1.2rem; color: #cbd5e1; }

.actions { display: flex; gap: 8px; margin-top: 1rem; }
.actions button {
    flex: 1;
    background: #6366f1;
    color: white;
    border: none;
    padding: 8px;
    border-radius: 8px;
    font-weight: 700;
    font-size: 0.75rem;
    cursor: pointer;
}
.actions button:hover { background: #4f46e5; }

js:
const { root, saveState, getState } = api;

async function init() {
    const saved = await getState();
    if (saved) {
        root.getElementById('raw-text').value = saved;
        encode();
    }
}

encode = async () => {
    const raw = root.getElementById('raw-text').value;
    try {
        const b64 = btoa(unescape(encodeURIComponent(raw)));
        root.getElementById('b64-text').value = b64;
        await saveState(raw);
    } catch(e) { }
};

decode = () => {
    const b64 = root.getElementById('b64-text').value;
    try {
        const raw = decodeURIComponent(escape(atob(b64)));
        root.getElementById('raw-text').value = raw;
    } catch(e) { }
};

copy = (id) => {
    const el = root.getElementById(id);
    el.select();
    document.execCommand('copy');
    alert("Copied to clipboard!");
};

init();
