css:
:host { display: block; margin: 1.5rem 0; font-family: var(--font-interface); }
.widget-card {
    background: #fdf2f8;
    color: #831843;
    border-radius: 20px;
    padding: 1.5rem;
    box-shadow: 0 10px 25px rgba(0,0,0,0.05);
    border: 1px solid #fce7f3;
    text-align: center;
}
.widget-title { margin: 0 0 1.5rem 0; font-size: 0.9rem; font-weight: 800; color: #db2777; text-transform: uppercase; letter-spacing: 1px; }

.input-grid {
    display: grid;
    grid-template-columns: 1fr 1fr;
    gap: 12px;
    margin-bottom: 1.5rem;
    text-align: left;
}
.input-group label { display: block; font-size: 0.65rem; font-weight: 700; color: #db2777; margin-bottom: 4px; text-transform: uppercase; }
.input-group input, .input-group select {
    width: 100%;
    background: white;
    border: 1px solid #f9a8d4;
    border-radius: 8px;
    padding: 6px 10px;
    font-size: 0.85rem;
    outline: none;
    color: #831843;
}

.btn-calc {
    width: 100%;
    background: #db2777;
    color: white;
    border: none;
    padding: 12px;
    border-radius: 12px;
    font-weight: 800;
    cursor: pointer;
    margin-bottom: 1.5rem;
}

.result-section {
    background: white;
    padding: 12px;
    border-radius: 12px;
    border: 1px solid #fce7f3;
    display: grid;
    gap: 8px;
}
.res-item { display: flex; justify-content: space-between; font-size: 0.9rem; }
.res-item b { color: #db2777; font-size: 1.1rem; }

js:
const { root, saveState, getState } = api;

function calculate() {
    const gender = root.getElementById('gender').value;
    const age = parseInt(root.getElementById('age').value);
    const weight = parseFloat(root.getElementById('weight').value);
    const height = parseFloat(root.getElementById('height').value);

    let bmr = 0;
    if (gender === 'm') {
        bmr = 88.362 + (13.397 * weight) + (4.799 * height) - (5.677 * age);
    } else {
        bmr = 447.593 + (9.247 * weight) + (3.098 * height) - (4.330 * age);
    }

    root.getElementById('bmr-val').innerText = Math.round(bmr);
    root.getElementById('tdee-val').innerText = Math.round(bmr * 1.2);

    saveState({ gender, age, weight, height });
}

async function init() {
    const saved = await getState();
    if (saved) {
        root.getElementById('gender').value = saved.gender || 'm';
        root.getElementById('age').value = saved.age || 25;
        root.getElementById('weight').value = saved.weight || 70;
        root.getElementById('height').value = saved.height || 175;
        calculate();
    }
}

init();
