:host {
    --budget-bg: var(--background-secondary);
    --budget-card-bg: var(--background-primary);
    --budget-accent: var(--interactive-accent);
    --budget-radius: 16px;
    --budget-padding: 1.25rem;
    --budget-font-size: 0.85rem;
    display: block;
    font-family: var(--font-interface);
}

* { box-sizing: border-box; }

.budget-container {
    background: var(--budget-bg);
    border: 1px solid var(--background-modifier-border);
    border-radius: var(--budget-radius);
    padding: var(--budget-padding);
    color: var(--text-normal);
    box-shadow: var(--shadow-s);
    width: 100%;
}

.widget-title {
    margin: 0 0 1.25rem 0;
    font-size: var(--budget-font-size);
    font-weight: 600;
    color: var(--text-muted);
    text-transform: uppercase;
    letter-spacing: 0.05em;
    text-align: center;
}

/* Balance Card */
.balance-card {
    background: var(--budget-card-bg);
    border-radius: calc(var(--budget-radius) - 4px);
    padding: var(--budget-padding);
    border: 1px solid var(--background-modifier-border);
    text-align: center;
    margin-bottom: 1.5rem;
}
.balance-label {
    font-size: 0.7rem;
    color: var(--text-muted);
    text-transform: uppercase;
    font-weight: 600;
    margin-bottom: 4px;
}
.balance-value {
    font-size: 2rem;
    font-weight: 800;
    color: var(--budget-accent);
    margin-bottom: 1rem;
}
.balance-value.negative { color: var(--text-error); }

.balance-stats {
    display: flex;
    justify-content: space-around;
    align-items: center;
    padding-top: 0.75rem;
    border-top: 1px solid var(--background-modifier-border);
}
.stat-item { display: flex; flex-direction: column; gap: 2px; }
.stat-label { font-size: 0.6rem; color: var(--text-muted); text-transform: uppercase; font-weight: 600; }
.stat-val { font-size: 0.9rem; font-weight: 700; }
.stat-item.income .stat-val { color: var(--text-success); }
.stat-item.expense .stat-val { color: var(--text-error); }
.stat-divider { width: 1px; height: 20px; background: var(--background-modifier-border); }

/* Input Section */
.input-section {
    display: flex;
    flex-direction: column;
    gap: 10px;
    margin-bottom: 1.5rem;
}
.input-row { display: flex; gap: 8px; }
.modern-input {
    background: var(--budget-card-bg);
    border: 1px solid var(--background-modifier-border);
    color: var(--text-normal);
    border-radius: 8px;
    padding: 8px 12px;
    font-size: var(--budget-font-size);
    outline: none;
    flex: 1;
}
.modern-input:focus { border-color: var(--budget-accent); }

.action-row {
    display: flex;
    justify-content: space-between;
    align-items: center;
    gap: 8px;
}
.type-toggle {
    display: flex;
    background: var(--budget-card-bg);
    border-radius: 8px;
    padding: 3px;
    border: 1px solid var(--background-modifier-border);
    flex: 1;
}
.toggle-btn {
    flex: 1;
    background: none;
    border: none;
    padding: 6px;
    font-size: 0.75rem;
    font-weight: 600;
    color: var(--text-muted);
    cursor: pointer;
    border-radius: 6px;
    transition: all 0.2s;
}
.toggle-btn.active {
    background: var(--budget-bg);
    color: var(--text-normal);
    box-shadow: var(--shadow-s);
}
#type-income.active { color: var(--text-success); }
#type-expense.active { color: var(--text-error); }

.add-btn {
    background: var(--budget-accent);
    color: var(--text-on-accent);
    border: none;
    padding: 8px 16px;
    border-radius: 8px;
    font-weight: 600;
    font-size: var(--budget-font-size);
    cursor: pointer;
}
.add-btn:hover { opacity: 0.9; }

/* Entry List */
.entry-list {
    display: flex;
    flex-direction: column;
    gap: 8px;
    max-height: 240px;
    overflow-y: auto;
    margin-bottom: 1.5rem;
    padding-right: 4px;
}
.entry-list::-webkit-scrollbar {
    width: 4px;
}
.entry-list::-webkit-scrollbar-thumb {
    background: var(--background-modifier-border);
    border-radius: 2px;
}

.entry-item {
    display: flex;
    justify-content: space-between;
    align-items: center;
    padding: 10px 12px;
    background: var(--budget-card-bg);
    border-radius: 10px;
    border: 1px solid var(--background-modifier-border);
    animation: slideIn 0.2s ease-out;
}
@keyframes slideIn {
    from { opacity: 0; transform: translateY(5px); }
    to { opacity: 1; transform: translateY(0); }
}

.entry-info {
    display: flex;
    flex-direction: column;
    gap: 2px;
}
.entry-name {
    font-size: var(--budget-font-size);
    font-weight: 600;
    color: var(--text-normal);
}
.entry-date {
    font-size: 0.65rem;
    color: var(--text-muted);
}

.entry-right {
    display: flex;
    align-items: center;
    gap: 12px;
}
.entry-amount { font-weight: 700; font-size: 0.9rem; }
.entry-amount.income { color: var(--text-success); }
.entry-amount.expense { color: var(--text-error); }

.btn-del {
    background: none;
    border: none;
    color: var(--text-muted);
    cursor: pointer;
    padding: 4px;
    display: flex;
    align-items: center;
    justify-content: center;
    border-radius: 4px;
}
.btn-del:hover {
    background: var(--background-modifier-error-hover);
    color: var(--text-error);
}

.footer {
    border-top: 1px solid var(--background-modifier-border);
    padding-top: 1rem;
    text-align: center;
}
.reset-btn {
    background: none;
    border: none;
    color: var(--text-muted);
    font-size: 0.7rem;
    font-weight: 600;
    cursor: pointer;
    text-decoration: underline;
}
.reset-btn:hover { color: var(--text-error); }

