const { root, saveState, getState } = api;
let entries = [];
let currentType = 'expense';

function setType(type) {
    currentType = type;
    root.getElementById('type-expense').classList.toggle('active', type === 'expense');
    root.getElementById('type-income').classList.toggle('active', type === 'income');
}

function formatCurrency(amount) {
    return new Intl.NumberFormat('en-US', {
        style: 'currency',
        currency: 'USD',
    }).format(amount);
}

function render() {
    const list = root.getElementById('entry-list');
    list.innerHTML = '';

    let total = 0;
    let income = 0;
    let expense = 0;

    entries.forEach((e, idx) => {
        total += e.amount;
        if (e.amount > 0) income += e.amount;
        else expense += Math.abs(e.amount);

        const div = document.createElement('div');
        div.className = 'entry-item';

        const isIncome = e.amount > 0;
        const dateStr = new Date(e.date).toLocaleDateString(undefined, { month: 'short', day: 'numeric' });

        div.innerHTML = `
            <div class="entry-info">
                <span class="entry-name">${e.name}</span>
                <span class="entry-date">${dateStr}</span>
            </div>
            <div class="entry-right">
                <span class="entry-amount ${isIncome ? 'income' : 'expense'}">
                    ${isIncome ? '+' : '-'}${formatCurrency(Math.abs(e.amount))}
                </span>
                <button class="btn-del" title="Delete">âœ•</button>
            </div>
        `;

        div.querySelector('.btn-del').onclick = () => deleteEntry(idx);
        list.appendChild(div);
    });

    const balEl = root.getElementById('total-balance');
    balEl.innerText = formatCurrency(total);
    balEl.classList.toggle('negative', total < 0);

    root.getElementById('total-income').innerText = formatCurrency(income);
    root.getElementById('total-expense').innerText = formatCurrency(expense);
}

function addEntry() {
    const nameInput = root.getElementById('exp-name');
    const amountInput = root.getElementById('exp-amount');

    const name = nameInput.value.trim();
    let amount = parseFloat(amountInput.value);

    if (name && !isNaN(amount) && amount > 0) {
        if (currentType === 'expense') amount = -amount;

        entries.unshift({
            name: name,
            amount: amount,
            date: Date.now()
        });

        if (entries.length > 50) entries.pop();

        nameInput.value = '';
        amountInput.value = '';

        render();
        saveState(entries);
    }
}

function deleteEntry(idx) {
    entries.splice(idx, 1);
    render();
    saveState(entries);
}

function resetBudget() {
    if (confirm('Are you sure you want to reset all budget data?')) {
        entries = [];
        render();
        saveState(entries);
    }
}

async function init() {
    const saved = await getState();
    if (Array.isArray(saved)) entries = saved;

    root.getElementById('btn-add').onclick = addEntry;
    root.getElementById('btn-reset').onclick = resetBudget;

    const handleEnter = (e) => { if (e.key === 'Enter') addEntry(); };
    root.getElementById('exp-name').onkeydown = handleEnter;
    root.getElementById('exp-amount').onkeydown = handleEnter;

    render();
}

init();
