css:
:host { display: block; margin: 1.5rem 0; font-family: var(--font-interface); }
.widget-card {
    background: #f8fafc;
    color: #1e293b;
    border-radius: 20px;
    padding: 1.5rem;
    box-shadow: 0 10px 25px rgba(0,0,0,0.05);
    border: 1px solid #e2e8f0;
    text-align: center;
}
.widget-title { margin: 0 0 1.5rem 0; font-size: 0.9rem; font-weight: 800; color: #64748b; text-transform: uppercase; letter-spacing: 1px; }

.balance-display { margin-bottom: 1.5rem; }
.balance { font-size: 2.5rem; font-weight: 900; color: #10b981; }
.balance.negative { color: #ef4444; }
.label { font-size: 0.75rem; font-weight: 700; color: #94a3b8; text-transform: uppercase; }

.input-row {
    display: flex;
    gap: 8px;
    margin-bottom: 1.5rem;
}
.input-row input { background: white; border: 1px solid #e2e8f0; border-radius: 8px; padding: 8px 12px; font-size: 0.85rem; outline: none; color: #1e293b; }
.input-row input[type="text"] { flex: 2; }
.input-row input[type="number"] { flex: 1; }
.input-row button { background: #1e293b; color: white; border: none; width: 36px; height: 36px; border-radius: 8px; font-weight: 800; cursor: pointer; }

.entry-list {
    text-align: left;
    display: grid;
    gap: 8px;
    max-height: 200px;
    overflow-y: auto;
    margin-bottom: 1.5rem;
}
.entry-item {
    display: flex;
    justify-content: space-between;
    align-items: center;
    padding: 8px 12px;
    background: white;
    border-radius: 8px;
    border: 1px solid #f1f5f9;
    font-size: 0.85rem;
    font-weight: 600;
}
.entry-info { flex: 1; }
.entry-amount { font-weight: 800; margin-right: 8px; }
.entry-amount.pos { color: #10b981; }
.entry-amount.neg { color: #ef4444; }
.btn-del { background: none; border: none; color: #ef4444; cursor: pointer; opacity: 0.5; }
.btn-del:hover { opacity: 1; }

.footer { border-top: 1px solid #f1f5f9; padding-top: 1rem; }
.btn-reset { background: none; border: none; color: #94a3b8; font-size: 0.7rem; font-weight: 700; cursor: pointer; text-decoration: underline; }

js:
var entries = [];

function render() {
    var list = api.root.getElementById('entry-list');
    list.innerHTML = '';

    var total = 0;
    entries.forEach(function(e, idx) {
        total += e.amount;
        var div = document.createElement('div');
        div.className = 'entry-item';
        div.innerHTML = '<span class="entry-info">' + e.name + '</span>' +
            '<span class="entry-amount ' + (e.amount >= 0 ? 'pos' : 'neg') + '">' + (e.amount >= 0 ? '+' : '') + '$' + Math.abs(e.amount).toFixed(2) + '</span>' +
            '<button class="btn-del">Ã—</button>';
        div.querySelector('.btn-del').onclick = function() { deleteEntry(idx); };
        list.appendChild(div);
    });

    var balEl = api.root.getElementById('total-balance');
    balEl.innerText = '$' + total.toFixed(2);
    balEl.classList.toggle('negative', total < 0);
}

function addEntry() {
    var name = api.root.getElementById('exp-name').value.trim();
    var amount = parseFloat(api.root.getElementById('exp-amount').value);

    if (name && !isNaN(amount)) {
        entries.unshift({ name: name, amount: amount });
        if (entries.length > 20) entries.pop();

        api.root.getElementById('exp-name').value = '';
        api.root.getElementById('exp-amount').value = '';

        render();
        api.saveState(entries);
    }
}

function deleteEntry(idx) {
    entries.splice(idx, 1);
    render();
    api.saveState(entries);
}

function resetBudget() {
    entries = [];
    render();
    api.saveState(entries);
}

async function init() {
    var saved = await api.getState();
    if (saved) entries = saved;

    api.root.getElementById('btn-add').onclick = addEntry;
    api.root.getElementById('btn-reset').onclick = resetBudget;
    api.root.getElementById('exp-name').onkeydown = function(e) { if (e.key === 'Enter') addEntry(); };

    render();
}

init();
