css:
:host { display: block; font-family: var(--font-interface); }
* { box-sizing: border-box; }

.char-container {
    background: var(--background-secondary);
    border: 1px solid var(--background-modifier-border);
    border-radius: 16px;
    padding: 1.25rem;
    color: var(--text-normal);
    box-shadow: var(--shadow-s);
    width: 100%;
}

.widget-title {
    margin: 0 0 1.25rem 0;
    font-size: 0.85rem;
    font-weight: 600;
    color: var(--text-muted);
    text-transform: uppercase;
    letter-spacing: 0.05em;
    text-align: center;
}

textarea {
    width: 100%;
    height: 140px;
    background: var(--background-primary);
    border: 1px solid var(--background-modifier-border);
    color: var(--text-normal);
    border-radius: 12px;
    padding: 12px;
    font-size: 0.9rem;
    outline: none;
    resize: vertical;
    margin-bottom: 1.25rem;
    font-family: var(--font-monospace);
    transition: border-color 0.2s;
    display: block;
}

textarea:focus {
    border-color: var(--interactive-accent);
}

.stats-grid {
    display: grid;
    grid-template-columns: repeat(4, 1fr);
    gap: 10px;
}

.stat-box {
    background: var(--background-primary);
    padding: 12px 4px;
    border-radius: 10px;
    border: 1px solid var(--background-modifier-border);
    text-align: center;
    display: flex;
    flex-direction: column;
    gap: 4px;
}

.stat-box span {
    display: block;
    font-size: 1.1rem;
    font-weight: 700;
    color: var(--interactive-accent);
}

.stat-box label {
    font-size: 0.65rem;
    font-weight: 600;
    text-transform: uppercase;
    color: var(--text-muted);
    letter-spacing: 0.02em;
}

js:
const { root, saveState, getState } = api;

function updateStats() {
    const text = root.getElementById('text-input').value;

    const chars = text.length;
    const words = text.trim() ? text.trim().split(/\s+/).length : 0;
    const lines = text ? text.split('\n').length : 0;

    // Reading time calculation (200 words per minute)
    const readTimeSec = Math.round((words / 200) * 60);
    let readTimeStr = "0s";

    if (words > 0) {
        if (readTimeSec < 60) {
            readTimeStr = Math.max(1, readTimeSec) + "s";
        } else {
            const mins = Math.floor(readTimeSec / 60);
            const secs = readTimeSec % 60;
            readTimeStr = secs > 0 ? `${mins}m ${secs}s` : `${mins}m`;
        }
    }

    root.getElementById('char-count').innerText = chars.toLocaleString();
    root.getElementById('word-count').innerText = words.toLocaleString();
    root.getElementById('line-count').innerText = lines.toLocaleString();
    root.getElementById('read-time').innerText = readTimeStr;
}

function save() {
    const text = root.getElementById('text-input').value;
    saveState(text);
}

async function init() {
    const saved = await getState();
    if (saved) {
        root.getElementById('text-input').value = saved;
        updateStats();
    }
}

init();
