css:
:host { display: block; margin: 1.5rem 0; font-family: var(--font-interface); }
.widget-card {
    background: #fdfcf0;
    color: #44403c;
    border-radius: 20px;
    padding: 1.5rem;
    box-shadow: 0 10px 25px rgba(0,0,0,0.05);
    border: 1px solid #e7e5e4;
    text-align: center;
}
.widget-title { margin: 0 0 1.5rem 0; font-size: 0.9rem; font-weight: 800; color: #78716c; text-transform: uppercase; letter-spacing: 1px; }

textarea {
    width: 100%;
    height: 120px;
    background: white;
    border: 1px solid #e7e5e4;
    color: #44403c;
    border-radius: 12px;
    padding: 12px;
    font-size: 0.9rem;
    outline: none;
    resize: vertical;
    margin-bottom: 1.5rem;
}

.stats-grid {
    display: grid;
    grid-template-columns: repeat(4, 1fr);
    gap: 8px;
}

.stat-box {
    background: white;
    padding: 10px 4px;
    border-radius: 10px;
    border: 1px solid #e7e5e4;
}
.stat-box span { display: block; font-size: 1.1rem; font-weight: 800; color: #44403c; }
.stat-box label { font-size: 0.6rem; font-weight: 700; text-transform: uppercase; color: #a8a29e; }

js:
const { root, saveState, getState } = api;

function updateStats() {
    const text = root.getElementById('text-input').value;

    const chars = text.length;
    const words = text.trim() ? text.trim().split(/\s+/).length : 0;
    const lines = text ? text.split('\n').length : 0;
    const readTime = Math.ceil(words / 200);

    root.getElementById('char-count').innerText = chars;
    root.getElementById('word-count').innerText = words;
    root.getElementById('line-count').innerText = lines;
    root.getElementById('read-time').innerText = readTime;

    saveState(text);
}

async function init() {
    const saved = await getState();
    if (saved) {
        root.getElementById('text-input').value = saved;
        updateStats();
    }
}

init();
