css:
:host { display: block; margin: 1.5rem 0; font-family: var(--font-interface); }
.widget-card {
    background: var(--background-secondary);
    border: 1px solid var(--divider-color);
    border-radius: 20px;
    padding: 1.5rem;
    text-align: center;
    position: relative;
}
.widget-title { margin: 0 0 1.5rem 0; font-size: 0.9rem; font-weight: 800; color: var(--text-muted); text-transform: uppercase; letter-spacing: 2px; }

.palette-container {
    display: flex;
    height: 150px;
    border-radius: 16px;
    overflow: hidden;
    margin-bottom: 1.5rem;
    border: 1px solid var(--divider-color);
}

.color-swatch {
    flex: 1;
    display: flex;
    align-items: flex-end;
    justify-content: center;
    padding-bottom: 12px;
    cursor: pointer;
    transition: flex 0.3s ease;
    position: relative;
}
.color-swatch:hover { flex: 1.5; }
.color-swatch span {
    font-size: 0.65rem;
    font-weight: 800;
    color: white;
    background: rgba(0,0,0,0.3);
    padding: 4px 8px;
    border-radius: 4px;
    opacity: 0;
    transition: opacity 0.2s;
}
.color-swatch:hover span { opacity: 1; }

.btn-gen {
    background: var(--interactive-accent);
    color: white;
    border: none;
    padding: 12px 24px;
    border-radius: 12px;
    font-weight: 700;
    cursor: pointer;
    transition: transform 0.2s;
}
.btn-gen:hover { transform: scale(1.05); }

.toast {
    position: absolute;
    bottom: 20px;
    left: 50%;
    transform: translateX(-50%);
    background: #10b981;
    color: white;
    padding: 6px 16px;
    border-radius: 100px;
    font-size: 0.8rem;
    font-weight: 700;
    opacity: 0;
    transition: opacity 0.3s;
    pointer-events: none;
}

js:
var colors = ['#264653', '#2a9d8f', '#e9c46a', '#f4a261', '#e76f51'];

function render() {
    var container = api.root.getElementById('palette');
    container.innerHTML = '';
    colors.forEach(function(c) {
        var swatch = document.createElement('div');
        swatch.className = 'color-swatch';
        swatch.style.background = c;
        swatch.onclick = function() { copyColor(c); };
        swatch.innerHTML = '<span>' + c + '</span>';
        container.appendChild(swatch);
    });
}

function generate() {
    colors = [];
    for (var i = 0; i < 5; i++) {
        colors.push('#' + Math.floor(Math.random()*16777215).toString(16).padStart(6, '0'));
    }
    render();
    api.saveState(colors);
}

function copyColor(color) {
    if (navigator.clipboard && navigator.clipboard.writeText) {
        navigator.clipboard.writeText(color).then(function() {
            var toast = api.root.getElementById('toast');
            toast.style.opacity = '1';
            setTimeout(function() { toast.style.opacity = '0'; }, 2000);
        });
    }
}

async function init() {
    var saved = await api.getState();
    if (saved) colors = saved;

    api.root.getElementById('btn-gen').onclick = generate;

    render();
}

init();
