css:
:host { display: block; margin: 1.5rem 0; font-family: var(--font-interface); }
.widget-card {
    background: var(--background-secondary);
    border: 1px solid var(--divider-color);
    border-radius: 20px;
    padding: 1.5rem;
    text-align: center;
    position: relative;
}
.widget-title { margin: 0 0 1.5rem 0; font-size: 0.9rem; font-weight: 800; color: var(--text-muted); text-transform: uppercase; letter-spacing: 2px; }

.palette-container {
    display: flex;
    height: 150px;
    border-radius: 16px;
    overflow: hidden;
    margin-bottom: 1.5rem;
    border: 1px solid var(--divider-color);
}

.color-swatch {
    flex: 1;
    display: flex;
    align-items: flex-end;
    justify-content: center;
    padding-bottom: 12px;
    cursor: pointer;
    transition: flex 0.3s ease;
    position: relative;
}
.color-swatch:hover { flex: 1.5; }
.color-swatch span {
    font-size: 0.65rem;
    font-weight: 800;
    color: white;
    background: rgba(0,0,0,0.3);
    padding: 4px 8px;
    border-radius: 4px;
    opacity: 0;
    transition: opacity 0.2s;
}
.color-swatch:hover span { opacity: 1; }

.btn-gen {
    background: var(--interactive-accent);
    color: white;
    border: none;
    padding: 12px 24px;
    border-radius: 12px;
    font-weight: 700;
    cursor: pointer;
    transition: transform 0.2s;
}
.btn-gen:hover { transform: scale(1.05); }

.toast {
    position: absolute;
    bottom: 20px;
    left: 50%;
    transform: translateX(-50%);
    background: #10b981;
    color: white;
    padding: 6px 16px;
    border-radius: 100px;
    font-size: 0.8rem;
    font-weight: 700;
    opacity: 0;
    transition: opacity 0.3s;
    pointer-events: none;
}

js:
const { root, saveState, getState } = api;

let colors = ['#264653', '#2a9d8f', '#e9c46a', '#f4a261', '#e76f51'];

async function init() {
    const saved = await getState();
    if (saved) colors = saved;
    render();
}

function render() {
    const container = root.getElementById('palette');
    container.innerHTML = '';
    colors.forEach(c => {
        const swatch = document.createElement('div');
        swatch.className = 'color-swatch';
        swatch.style.background = c;
        swatch.onclick = () => copyColor(c);
        swatch.innerHTML = `<span>${c}</span>`;
        container.appendChild(swatch);
    });
}

generate = async () => {
    colors = [];
    for (let i = 0; i < 5; i++) {
        colors.push('#' + Math.floor(Math.random()*16777215).toString(16).padStart(6, '0'));
    }
    render();
    await saveState(colors);
};

copyColor = (color) => {
    navigator.clipboard.writeText(color).then(() => {
        const toast = root.getElementById('toast');
        toast.style.opacity = '1';
        setTimeout(() => toast.style.opacity = '0', 2000);
    });
};

init();
