css:
:host { display: block; margin: 1.5rem 0; font-family: var(--font-interface); }
.widget-card {
    background: linear-gradient(135deg, #1e293b 0%, #0f172a 100%);
    color: white;
    border-radius: 24px;
    padding: 1.5rem;
    box-shadow: 0 10px 30px rgba(0,0,0,0.3);
    text-align: center;
}
.widget-title { margin: 0 0 1.5rem 0; font-size: 1rem; font-weight: 800; color: #f59e0b; text-transform: uppercase; letter-spacing: 1px; }

.countdown-grid {
    display: grid;
    grid-template-columns: repeat(4, 1fr);
    gap: 10px;
    margin-bottom: 2rem;
}

.time-box {
    background: rgba(255,255,255,0.05);
    padding: 12px 4px;
    border-radius: 12px;
    border: 1px solid rgba(255,255,255,0.1);
}
.time-box span {
    display: block;
    font-size: 1.5rem;
    font-weight: 900;
    font-family: var(--font-monospace);
    color: #f59e0b;
}
.time-box label { font-size: 0.6rem; font-weight: 700; text-transform: uppercase; opacity: 0.6; }

.settings {
    display: grid;
    gap: 8px;
    background: rgba(0,0,0,0.2);
    padding: 12px;
    border-radius: 16px;
}
.settings input {
    background: rgba(255,255,255,0.1);
    border: 1px solid rgba(255,255,255,0.2);
    color: white;
    padding: 8px;
    border-radius: 8px;
    font-size: 0.85rem;
    outline: none;
}
.settings button {
    background: #f59e0b;
    color: white;
    border: none;
    padding: 10px;
    border-radius: 8px;
    font-weight: 800;
    cursor: pointer;
}

js:
const { root, saveState, getState } = api;

let state = {
    title: "Next Vacation",
    targetDate: new Date(Date.now() + 1000 * 60 * 60 * 24 * 30).toISOString().split('T')[0]
};
let timerId = null;

function startCountdown() {
    if (timerId) clearInterval(timerId);

    root.getElementById('event-title').innerText = `⏳ ${state.title}`;

    timerId = setInterval(() => {
        const now = new Date().getTime();
        const target = new Date(state.targetDate).getTime();
        const diff = target - now;

        if (diff <= 0) {
            clearInterval(timerId);
            updateDisplay(0, 0, 0, 0);
            return;
        }

        const days = Math.floor(diff / (1000 * 60 * 60 * 24));
        const hours = Math.floor((diff % (1000 * 60 * 60 * 24)) / (1000 * 60 * 60));
        const mins = Math.floor((diff % (1000 * 60 * 60)) / (1000 * 60));
        const secs = Math.floor((diff % (1000 * 60)) / 1000);

        updateDisplay(days, hours, mins, secs);
    }, 1000);
}

function updateDisplay(d, h, m, s) {
    root.getElementById('days').innerText = d.toString().padStart(2, '0');
    root.getElementById('hours').innerText = h.toString().padStart(2, '0');
    root.getElementById('mins').innerText = m.toString().padStart(2, '0');
    root.getElementById('secs').innerText = s.toString().padStart(2, '0');
}

function updateEvent() {
    state.title = root.getElementById('event-name').value.trim() || "Event";
    state.targetDate = root.getElementById('event-date').value;

    if (state.targetDate) {
        startCountdown();
        saveState(state);
    }
}

async function init() {
    const saved = await getState();
    if (saved) state = saved;

    root.getElementById('event-name').value = state.title;
    root.getElementById('event-date').value = state.targetDate;

    startCountdown();
}

init();
