css:
:host { display: block; margin: 1.5rem 0; font-family: var(--font-interface); }
.widget-card {
    background: #0f172a;
    color: #f8fafc;
    border-radius: 20px;
    padding: 1.5rem;
    box-shadow: 0 10px 25px rgba(0,0,0,0.3);
    border: 1px solid rgba(255,255,255,0.1);
}
.widget-header { display: flex; justify-content: space-between; align-items: center; margin-bottom: 1.5rem; }
.widget-title { margin: 0; font-size: 1.1rem; font-weight: 800; color: #38bdf8; text-transform: uppercase; letter-spacing: 1px; }

.btn-refresh {
    background: rgba(56, 189, 248, 0.1);
    color: #38bdf8;
    border: 1px solid rgba(56, 189, 248, 0.2);
    width: 32px;
    height: 32px;
    border-radius: 8px;
    cursor: pointer;
    display: flex;
    align-items: center;
    justify-content: center;
    font-size: 1.2rem;
    transition: all 0.3s;
}
.btn-refresh:hover { background: rgba(56, 189, 248, 0.2); transform: rotate(180deg); }

.crypto-list { display: grid; gap: 12px; }
.crypto-item {
    background: rgba(255,255,255,0.03);
    border: 1px solid rgba(255,255,255,0.05);
    border-radius: 12px;
    padding: 12px 16px;
    display: flex;
    justify-content: space-between;
    align-items: center;
    transition: transform 0.2s;
}
.crypto-item:hover { transform: scale(1.02); background: rgba(255,255,255,0.05); }

.coin-info { display: flex; align-items: center; gap: 12px; }
.coin-icon { font-size: 1.5rem; width: 32px; text-align: center; }
.coin-name { font-weight: 600; font-size: 0.95rem; }

.coin-price { text-align: right; }
.price { display: block; font-weight: 700; font-size: 1rem; }
.change { font-size: 0.75rem; font-weight: 600; }
.change.up { color: #4ade80; }
.change.down { color: #f87171; }

.last-update { 
    margin-top: 1.5rem; 
    font-size: 0.7rem; 
    color: #94a3b8; 
    text-align: center; 
    font-style: italic; 
}

js:
const { root, saveState, getState } = api;

let state = {
    btc: { price: 65432.10, change: 2.5 },
    eth: { price: 3456.78, change: -1.2 },
    sol: { price: 145.67, change: 5.4 },
    lastUpdated: null
};

async function init() {
    const saved = await getState();
    if (saved) state = saved;
    updateUI();
}

function updateUI() {
    updateCoin('btc', state.btc);
    updateCoin('eth', state.eth);
    updateCoin('sol', state.sol);

    root.getElementById('last-update').innerText = state.lastUpdated 
        ? `Last updated: ${state.lastUpdated}` 
        : 'Last updated: Never';
}

function updateCoin(id, data) {
    const priceEl = root.getElementById(`${id}-price`);
    const changeEl = root.getElementById(`${id}-change`);

    priceEl.innerText = `$${data.price.toLocaleString(undefined, {minimumFractionDigits: 2})}`;
    changeEl.innerText = `${data.change >= 0 ? '+' : ''}${data.change}%`;
    changeEl.className = `change ${data.change >= 0 ? 'up' : 'down'}`;
}

refreshPrices = async () => {
    // Simulate API call
    state.btc.price += (Math.random() - 0.5) * 500;
    state.btc.change = ((Math.random() - 0.4) * 5).toFixed(1);

    state.eth.price += (Math.random() - 0.5) * 50;
    state.eth.change = ((Math.random() - 0.4) * 5).toFixed(1);

    state.sol.price += (Math.random() - 0.5) * 5;
    state.sol.change = ((Math.random() - 0.4) * 8).toFixed(1);

    state.lastUpdated = new Date().toLocaleTimeString();

    updateUI();
    await saveState(state);
};

init();
