:host { display: block; font-family: var(--font-interface); }
* { box-sizing: border-box; }

.cyber-widget {
    background: var(--background-secondary);
    border: 1px solid var(--background-modifier-border);
    border-radius: 16px;
    padding: 1.25rem;
    color: var(--text-normal);
    box-shadow: var(--shadow-s);
    width: 100%;
}

.widget-header {
    display: flex;
    justify-content: space-between;
    align-items: center;
    margin-bottom: 1rem;
}

.widget-title {
    margin: 0;
    font-size: 0.85rem;
    font-weight: 600;
    color: var(--text-muted);
    text-transform: uppercase;
    letter-spacing: 0.1em;
}

.header-actions { display: flex; gap: 8px; }
.btn-icon {
    background: var(--background-primary);
    border: 1px solid var(--background-modifier-border);
    color: var(--text-muted);
    width: 28px;
    height: 28px;
    border-radius: 6px;
    cursor: pointer;
    display: flex;
    align-items: center;
    justify-content: center;
    font-size: 0.9rem;
    transition: all 0.2s;
}
.btn-icon:hover { background: var(--background-modifier-hover); color: var(--interactive-accent); }

.converter-layout {
    display: flex;
    flex-direction: column;
    gap: 1rem;
}

.field-group {
    display: flex;
    flex-direction: column;
    gap: 8px;
}

.field-header {
    display: flex;
    justify-content: space-between;
    align-items: center;
}

.field-header label {
    font-size: 0.7rem;
    font-weight: 700;
    color: var(--text-muted);
    text-transform: uppercase;
    letter-spacing: 0.05em;
}

.mode-select {
    background: var(--background-primary);
    border: 1px solid var(--background-modifier-border);
    color: var(--text-normal);
    border-radius: 6px;
    padding: 4px 8px;
    font-size: 0.75rem;
    outline: none;
    cursor: pointer;
}

.cyber-area {
    width: 100%;
    height: 100px;
    background: var(--background-primary);
    border: 1px solid var(--background-modifier-border);
    color: var(--text-normal);
    border-radius: 10px;
    padding: 12px;
    font-size: 0.85rem;
    font-family: var(--font-monospace);
    outline: none;
    resize: none;
    transition: border-color 0.2s;
}
.cyber-area:focus { border-color: var(--interactive-accent); }
.cyber-area.output {
    background: var(--background-secondary-alt);
    color: var(--text-accent);
}

.copy-btn {
    background: transparent;
    border: 1px solid var(--interactive-accent);
    color: var(--interactive-accent);
    padding: 2px 8px;
    border-radius: 4px;
    font-size: 0.65rem;
    font-weight: 600;
    cursor: pointer;
    transition: all 0.2s;
}
.copy-btn:hover { background: var(--interactive-accent); color: var(--text-on-accent); }

.toast {
    position: fixed;
    bottom: 20px;
    left: 50%;
    transform: translateX(-50%) translateY(100px);
    background: var(--interactive-accent);
    color: var(--text-on-accent);
    padding: 8px 16px;
    border-radius: 20px;
    font-size: 0.8rem;
    font-weight: 600;
    transition: transform 0.3s cubic-bezier(0.18, 0.89, 0.32, 1.28);
    z-index: 100;
}
.toast.show { transform: translateX(-50%) translateY(0); }

js:
const { root, saveState, getState } = api;

function convert() {
    const input = root.getElementById('input-text').value;
    const mode = root.getElementById('mode-select').value;
    const output = root.getElementById('output-text');

    if (!input) {
        output.value = '';
        return;
    }

    try {
        let result = '';
        switch (mode) {
            case 'b64-enc': result = btoa(unescape(encodeURIComponent(input))); break;
            case 'b64-dec': result = decodeURIComponent(escape(atob(input))); break;
            case 'hex-enc': result = Array.from(input).map(c => c.charCodeAt(0).toString(16).padStart(2, '0')).join(' '); break;
            case 'hex-dec': result = input.split(/\s+/).map(h => String.fromCharCode(parseInt(h, 16))).join(''); break;
            case 'bin-enc': result = Array.from(input).map(c => c.charCodeAt(0).toString(2).padStart(8, '0')).join(' '); break;
            case 'bin-dec': result = input.split(/\s+/).map(b => String.fromCharCode(parseInt(b, 2))).join(''); break;
            case 'url-enc': result = encodeURIComponent(input); break;
            case 'url-dec': result = decodeURIComponent(input); break;
            case 'html-enc': result = input.replace(/[&<>'"/]/g, m => ({'&':'&amp;','<':'&lt;','>':'&gt;','\'':'&#39;','"':'&quot;','/':'&#47;'}[m])); break;
            case 'html-dec': 
                const doc = new DOMParser().parseFromString(input, 'text/html');
                result = doc.documentElement.textContent;
                break;
            case 'rot13': result = input.replace(/[a-zA-Z]/g, c => String.fromCharCode((c <= 'Z' ? 90 : 122) >= (c = c.charCodeAt(0) + 13) ? c : c - 26)); break;
        }
        output.value = result;
    } catch (e) {
        output.value = '‚ö†Ô∏è Error: Invalid format for ' + mode;
    }
}

function save() {
    const input = root.getElementById('input-text').value;
    const mode = root.getElementById('mode-select').value;
    saveState({ input, mode });
}

function swap() {
    const inputEl = root.getElementById('input-text');
    const outputEl = root.getElementById('output-text');
    const modeEl = root.getElementById('mode-select');

    const temp = inputEl.value;
    inputEl.value = outputEl.value;

    // Try to invert mode
    const currentMode = modeEl.value;
    if (currentMode.endsWith('-enc')) modeEl.value = currentMode.replace('-enc', '-dec');
    else if (currentMode.endsWith('-dec')) modeEl.value = currentMode.replace('-dec', '-enc');

    convert();
    save();
}

function clearAll() {
    root.getElementById('input-text').value = '';
    root.getElementById('output-text').value = '';
    convert();
    save();
}

function copyResult() {
    const output = root.getElementById('output-text').value;
    if (!output) return;
    navigator.clipboard.writeText(output);
    showToast();
}

function showToast() {
    const toast = root.getElementById('toast');
    toast.classList.add('show');
    setTimeout(() => toast.classList.remove('show'), 2000);
}

async function init() {
    const saved = await getState();
    const inputEl = root.getElementById('input-text');
    const modeEl = root.getElementById('mode-select');

    if (saved) {
        if (saved.input) inputEl.value = saved.input;
        if (saved.mode) modeEl.value = saved.mode;
    }

    inputEl.oninput = convert;
    inputEl.onblur = save;
    modeEl.onchange = () => { convert(); save(); };

    root.getElementById('btn-swap').onclick = swap;
    root.getElementById('btn-clear').onclick = clearAll;
    root.getElementById('btn-copy').onclick = copyResult;

    convert();
}

init();
