css:
:host { display: block; margin: 1.5rem 0; font-family: var(--font-interface); }
.widget-card {
    background: var(--background-secondary);
    color: var(--text-normal);
    border-radius: 16px;
    padding: 2rem;
    box-shadow: var(--shadow-s);
    border: 1px solid var(--background-modifier-border);
    text-align: center;
    position: relative;
    overflow: hidden;
}

.quote-icon {
    position: absolute;
    top: -10px;
    left: 20px;
    font-size: 6rem;
    opacity: 0.05;
    font-family: serif;
    color: var(--interactive-accent);
}

.quote-container {
    min-height: 120px;
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    margin-bottom: 1.5rem;
}

.affirmation {
    font-size: 1.2rem;
    font-weight: 600;
    line-height: 1.5;
    margin-bottom: 0.5rem;
    color: var(--text-normal);
}

.author {
    font-size: 0.9rem;
    font-style: italic;
    color: var(--text-muted);
    font-weight: 500;
}

.btn-next {
    background: var(--interactive-accent);
    color: var(--text-on-accent);
    border: none;
    padding: 10px 20px;
    border-radius: 8px;
    font-weight: 600;
    font-size: 0.8rem;
    cursor: pointer;
    transition: opacity 0.2s;
}
.btn-next:hover { opacity: 0.8; }

.footer {
    margin-top: 1.5rem;
    font-size: 0.65rem;
    color: var(--text-faint);
    font-weight: 500;
}

js:
const { root, saveState, getState } = api;
const QUOTES_URL = 'https://raw.githubusercontent.com/dwyl/quotes/main/quotes.json';
let quotes = [];
let updateInterval;

async function fetchQuotes() {
    try {
        const response = await fetch(QUOTES_URL);
        quotes = await response.json();
        return true;
    } catch (e) {
        console.error('Failed to fetch quotes', e);
        root.getElementById('affirmation-text').innerText = 'Failed to load quotes.';
        return false;
    }
}

function displayRandomQuote() {
    if (quotes.length === 0) return;

    const quote = quotes[Math.floor(Math.random() * quotes.length)];
    const time = new Date().toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' });

    root.getElementById('affirmation-text').innerText = quote.text;
    root.getElementById('quote-author').innerText = quote.author ? '- ' + quote.author : '';
    root.getElementById('last-updated').innerText = 'Last updated: ' + time;

    saveState({ quote, time });
}

async function init() {
    const saved = await getState();
    if (saved && saved.quote) {
        root.getElementById('affirmation-text').innerText = saved.quote.text;
        root.getElementById('quote-author').innerText = saved.quote.author ? '- ' + saved.quote.author : '';
        root.getElementById('last-updated').innerText = 'Last updated: ' + saved.time;
    }

    const success = await fetchQuotes();
    if (success) {
        if (!saved) displayRandomQuote();

        // Update every 10 minutes
        clearInterval(updateInterval);
        updateInterval = setInterval(displayRandomQuote, 10 * 60 * 1000);
    }

    root.getElementById('btn-next').onclick = displayRandomQuote;
}

init();
