css:
:host { display: block; margin: 1.5rem 0; font-family: var(--font-interface); }
.widget-card {
    background: #fffbeb;
    color: #92400e;
    border-radius: 20px;
    padding: 1.5rem;
    box-shadow: 0 10px 25px rgba(0,0,0,0.05);
    border: 1px solid #fef3c7;
    text-align: center;
}
.widget-title { margin: 0; font-size: 1.1rem; font-weight: 800; color: #d97706; }
.subtitle { font-size: 0.8rem; margin: 4px 0 1.5rem 0; opacity: 0.7; font-weight: 600; }

.input-list { display: grid; gap: 10px; margin-bottom: 1.5rem; }
.input-item {
    display: flex;
    align-items: center;
    gap: 10px;
    background: white;
    padding: 8px 12px;
    border-radius: 12px;
    border: 1px solid #fde68a;
}
.input-item span { font-weight: 800; color: #f59e0b; }
.input-item input {
    border: none;
    background: none;
    flex: 1;
    font-size: 0.9rem;
    color: #92400e;
    outline: none;
    font-weight: 500;
}

.btn-save {
    background: #f59e0b;
    color: white;
    border: none;
    padding: 10px 24px;
    border-radius: 100px;
    font-weight: 700;
    cursor: pointer;
    transition: all 0.2s;
}
.btn-save:hover { background: #d97706; transform: translateY(-2px); }

.history {
    margin-top: 1.5rem;
    text-align: left;
    max-height: 150px;
    overflow-y: auto;
    font-size: 0.75rem;
    display: grid;
    gap: 8px;
}
.history-item {
    background: rgba(255,255,255,0.5);
    padding: 8px;
    border-radius: 8px;
    border-left: 3px solid #f59e0b;
}

js:
const { root, saveState, getState } = api;

let state = {
    current: ['', '', ''],
    history: []
};

function renderHistory() {
    const hist = root.getElementById('grat-history');
    hist.innerHTML = '';
    state.history.slice(0, 5).forEach(item => {
        const div = document.createElement('div');
        div.className = 'history-item';
        div.innerHTML = `<b>${item.date}:</b> ${item.items.join(', ')}`;
        hist.appendChild(div);
    });
}

function saveGratitude() {
    const items = [
        root.getElementById('grat-1').value.trim(),
        root.getElementById('grat-2').value.trim(),
        root.getElementById('grat-3').value.trim()
    ];

    if (items.some(i => i !== '')) {
        state.current = items;
        const date = new Date().toLocaleDateString();
        if (state.history.length === 0 || state.history[0].date !== date) {
            state.history.unshift({ date, items });
        } else {
            state.history[0].items = items;
        }

        renderHistory();
        saveState(state);
    }
}

async function init() {
    const saved = await getState();
    if (saved) state = saved;

    root.getElementById('grat-1').value = state.current[0] || '';
    root.getElementById('grat-2').value = state.current[1] || '';
    root.getElementById('grat-3').value = state.current[2] || '';

    renderHistory();
}

init();
