css:
:host { display: block; margin: 1.5rem 0; font-family: var(--font-interface); }
.widget-card {
    background: #ffffff;
    color: #1e293b;
    border-radius: 20px;
    padding: 2rem;
    text-align: center;
    box-shadow: 0 10px 25px rgba(0,0,0,0.05);
    border: 1px solid #e2e8f0;
    position: relative;
    overflow: hidden;
}

.quote-icon {
    position: absolute;
    top: -10px;
    left: 20px;
    font-size: 8rem;
    color: #f1f5f9;
    font-family: serif;
    z-index: 0;
}

.quote-content {
    position: relative;
    z-index: 1;
    margin-bottom: 1.5rem;
}

#quote-text {
    font-size: 1.25rem;
    font-weight: 600;
    line-height: 1.6;
    margin-bottom: 1rem;
    font-style: italic;
    color: #334155;
}

#quote-author {
    font-size: 0.9rem;
    font-weight: 700;
    color: #64748b;
    text-transform: uppercase;
    letter-spacing: 1px;
}
#quote-author::before { content: "— "; }

.btn-next {
    background: #1e293b;
    color: white;
    border: none;
    padding: 10px 24px;
    border-radius: 100px;
    font-weight: 700;
    cursor: pointer;
    transition: all 0.2s;
}
.btn-next:hover { background: #334155; transform: scale(1.05); }

js:
const { root, saveState, getState } = api;

const quotes = [
    { text: "The only way to do great work is to love what you do.", author: "Steve Jobs" },
    { text: "Innovation distinguishes between a leader and a follower.", author: "Steve Jobs" },
    { text: "Your time is limited, so don't waste it living someone else's life.", author: "Steve Jobs" },
    { text: "Stay hungry, stay foolish.", author: "Steve Jobs" },
    { text: "The future belongs to those who believe in the beauty of their dreams.", author: "Eleanor Roosevelt" },
    { text: "It does not matter how slowly you go as long as you do not stop.", author: "Confucius" },
    { text: "Everything you've ever wanted is on the other side of fear.", author: "George Addair" },
    { text: "Success is not final, failure is not fatal: it is the courage to continue that counts.", author: "Winston Churchill" },
    { text: "Hardships often prepare ordinary people for an extraordinary destiny.", author: "C.S. Lewis" },
    { text: "Believe you can and you're halfway there.", author: "Theodore Roosevelt" }
];

let currentIdx = 0;

function display() {
    const q = quotes[currentIdx];
    root.getElementById('quote-text').innerText = q.text;
    root.getElementById('quote-author').innerText = q.author;
}

function nextQuote() {
    let next;
    do {
        next = Math.floor(Math.random() * quotes.length);
    } while (next === currentIdx);

    currentIdx = next;
    display();
    saveState(currentIdx);
}

async function init() {
    const saved = await getState();
    if (saved !== null) currentIdx = saved;
    else currentIdx = Math.floor(Math.random() * quotes.length);
    display();
}

init();
