css:
:host { display: block; margin: 1.5rem 0; font-family: var(--font-interface); }
.widget-card {
    background: #064e3b;
    color: #d1fae5;
    border-radius: 20px;
    padding: 1.5rem;
    box-shadow: 0 10px 25px rgba(0,0,0,0.3);
    border: 1px solid #065f46;
    text-align: center;
}
.widget-title { margin: 0 0 1.5rem 0; font-size: 0.9rem; font-weight: 800; color: #10b981; text-transform: uppercase; letter-spacing: 2px; }

.result-box {
    font-size: 2rem;
    font-weight: 900;
    color: white;
    height: 80px;
    display: flex;
    align-items: center;
    justify-content: center;
    margin-bottom: 1.5rem;
    background: rgba(0,0,0,0.2);
    border-radius: 16px;
    text-shadow: 0 0 10px rgba(16, 185, 129, 0.5);
}

.options-list { display: grid; gap: 8px; margin-bottom: 1.5rem; max-height: 150px; overflow-y: auto; }
.option-item {
    display: flex;
    gap: 8px;
}
.option-item input {
    flex: 1;
    background: rgba(255,255,255,0.1);
    border: 1px solid rgba(255,255,255,0.2);
    color: white;
    padding: 8px 12px;
    border-radius: 8px;
    font-size: 0.85rem;
    outline: none;
}
.option-item .btn-del {
    background: rgba(239,68,68,0.3);
    border: none;
    color: #fecaca;
    width: 32px;
    border-radius: 8px;
    cursor: pointer;
}
.option-item .btn-del:hover { background: rgba(239,68,68,0.5); }

.controls { display: flex; gap: 10px; }
.btn-add { flex: 1; background: none; border: 1px solid #10b981; color: #10b981; padding: 10px; border-radius: 10px; font-weight: 700; cursor: pointer; }
.btn-decide { flex: 1.5; background: #10b981; color: white; border: none; padding: 10px; border-radius: 10px; font-weight: 800; cursor: pointer; box-shadow: 0 4px 0 #059669; }
.btn-decide:active { transform: translateY(2px); box-shadow: 0 2px 0 #059669; }

js:
var options = ['Yes', 'No'];

function render() {
    var list = api.root.getElementById('options-list');
    list.innerHTML = '';
    options.forEach(function(opt, idx) {
        var div = document.createElement('div');
        div.className = 'option-item';

        var input = document.createElement('input');
        input.type = 'text';
        input.value = opt;
        input.oninput = function(e) {
            options[idx] = e.target.value;
            api.saveState(options);
        };

        var delBtn = document.createElement('button');
        delBtn.className = 'btn-del';
        delBtn.innerText = 'Ã—';
        delBtn.onclick = function() { deleteOption(idx); };

        div.appendChild(input);
        div.appendChild(delBtn);
        list.appendChild(div);
    });
}

function addOption() {
    options.push('');
    render();
    api.saveState(options);
}

function deleteOption(idx) {
    options.splice(idx, 1);
    render();
    api.saveState(options);
}

function decide() {
    var validOptions = options.filter(function(o) { return o.trim() !== ''; });
    if (validOptions.length === 0) return;

    var resultBox = api.root.getElementById('decision-result');
    var count = 0;
    var interval = setInterval(function() {
        resultBox.innerText = validOptions[Math.floor(Math.random() * validOptions.length)];
        count++;
        if (count > 10) {
            clearInterval(interval);
            resultBox.style.color = '#10b981';
            setTimeout(function() { resultBox.style.color = 'white'; }, 1000);
        }
    }, 50);
}

async function init() {
    var saved = await api.getState();
    if (saved) options = saved;

    api.root.getElementById('btn-add').onclick = addOption;
    api.root.getElementById('btn-decide').onclick = decide;

    render();
}

init();
