css:
:host { display: block; margin: 1.5rem 0; font-family: var(--font-interface); }
.widget-card {
    background: #450a0a;
    color: #fecaca;
    border-radius: 20px;
    padding: 1.5rem;
    box-shadow: 0 10px 25px rgba(0,0,0,0.3);
    border: 1px solid #7f1d1d;
    text-align: center;
}
.widget-title { margin: 0 0 1.5rem 0; font-size: 0.9rem; font-weight: 800; color: #ef4444; text-transform: uppercase; letter-spacing: 2px; }

.dice-display {
    font-size: 4rem;
    font-weight: 900;
    color: white;
    height: 100px;
    display: flex;
    align-items: center;
    justify-content: center;
    margin-bottom: 1.5rem;
    text-shadow: 0 0 20px rgba(239, 68, 68, 0.5);
}

.dice-grid {
    display: grid;
    grid-template-columns: repeat(4, 1fr);
    gap: 8px;
    margin-bottom: 1.5rem;
}
.dice-grid button {
    background: #7f1d1d;
    color: white;
    border: none;
    padding: 10px 4px;
    border-radius: 8px;
    font-weight: 800;
    font-size: 0.8rem;
    cursor: pointer;
    transition: all 0.1s;
}
.dice-grid button:active { transform: scale(0.9); background: #ef4444; }
.btn-clear { background: #374151 !important; }

.history {
    display: flex;
    gap: 8px;
    overflow-x: auto;
    padding-bottom: 8px;
    font-size: 0.7rem;
    font-weight: 700;
    opacity: 0.6;
}
.history span { background: rgba(255,255,255,0.1); padding: 4px 8px; border-radius: 4px; }

js:
var history = [];

function renderHistory() {
    var hist = api.root.getElementById('roll-history');
    hist.innerHTML = '';
    history.slice(0, 10).forEach(function(val) {
        var span = document.createElement('span');
        span.innerText = val;
        hist.appendChild(span);
    });
}

function roll(sides) {
    var display = api.root.getElementById('dice-result');
    var result = Math.floor(Math.random() * sides) + 1;
    display.innerText = result;

    history.unshift('D' + sides + ': ' + result);
    if (history.length > 10) history.pop();

    renderHistory();
    api.saveState(history);
}

function clearHistory() {
    history = [];
    api.root.getElementById('dice-result').innerText = '?';
    renderHistory();
    api.saveState(history);
}

async function init() {
    var saved = await api.getState();
    if (saved) history = saved;

    api.root.getElementById('btn-d4').onclick = function() { roll(4); };
    api.root.getElementById('btn-d6').onclick = function() { roll(6); };
    api.root.getElementById('btn-d8').onclick = function() { roll(8); };
    api.root.getElementById('btn-d10').onclick = function() { roll(10); };
    api.root.getElementById('btn-d12').onclick = function() { roll(12); };
    api.root.getElementById('btn-d20').onclick = function() { roll(20); };
    api.root.getElementById('btn-d100').onclick = function() { roll(100); };
    api.root.getElementById('btn-clear').onclick = clearHistory;

    renderHistory();
}

init();
