css:
:host { display: block; margin: 1.5rem 0; font-family: var(--font-interface); }
.widget-card {
    background: var(--background-secondary);
    border: 1px solid var(--divider-color);
    border-radius: 16px;
    padding: 1.5rem;
    box-shadow: 0 4px 15px rgba(0,0,0,0.05);
}
.widget-header { display: flex; justify-content: space-between; align-items: center; margin-bottom: 1.5rem; }
.widget-title { margin: 0; font-size: 1rem; font-weight: 700; color: var(--text-normal); }

.total-display {
    font-size: 1.5rem;
    font-weight: 800;
    color: var(--interactive-accent);
}

.input-section {
    display: flex;
    gap: 8px;
    margin-bottom: 1.5rem;
}
.input-section input {
    background: var(--background-primary);
    border: 1px solid var(--divider-color);
    color: var(--text-normal);
    border-radius: 8px;
    padding: 8px 12px;
    font-size: 0.85rem;
    outline: none;
}
#exp-name { flex: 2; }
#exp-amount { flex: 1; }

.btn-add {
    background: var(--interactive-accent);
    color: white;
    border: none;
    border-radius: 8px;
    padding: 0 16px;
    font-weight: 700;
    cursor: pointer;
}

.expense-list {
    max-height: 200px;
    overflow-y: auto;
    margin-bottom: 1rem;
    display: grid;
    gap: 8px;
}

.expense-item {
    display: flex;
    justify-content: space-between;
    align-items: center;
    padding: 10px 12px;
    background: var(--background-primary-alt);
    border-radius: 10px;
    border: 1px solid var(--divider-color);
}

.exp-info { display: flex; flex-direction: column; }
.exp-title { font-weight: 600; font-size: 0.9rem; }
.exp-date { font-size: 0.7rem; color: var(--text-muted); }

.exp-right { display: flex; align-items: center; gap: 12px; }
.exp-val { font-weight: 700; color: var(--text-error); }
.btn-del { background: none; border: none; color: var(--text-error); cursor: pointer; opacity: 0.5; font-size: 1rem; }
.btn-del:hover { opacity: 1; }

.footer-actions { text-align: right; }
.btn-clear { 
    background: none; 
    border: none; 
    color: var(--text-muted); 
    font-size: 0.75rem; 
    cursor: pointer; 
    text-decoration: underline; 
}
.btn-clear:hover { color: var(--text-error); }

js:
var expenses = [];

function render() {
    var list = api.root.getElementById('expense-list');
    var totalEl = api.root.getElementById('total-amount');
    list.innerHTML = '';

    var total = 0;
    expenses.forEach(function(exp, idx) {
        total += parseFloat(exp.amount);
        var item = document.createElement('div');
        item.className = 'expense-item';
        item.innerHTML = '<div class="exp-info"><span class="exp-title">' + exp.name + '</span><span class="exp-date">' + exp.date + '</span></div><div class="exp-right"><span class="exp-val">-$' + parseFloat(exp.amount).toFixed(2) + '</span><button class="btn-del">Ã—</button></div>';

        item.querySelector('.btn-del').onclick = function() { deleteExpense(idx); };
        list.appendChild(item);
    });

    totalEl.innerText = '$' + total.toFixed(2);
}

function addExpense() {
    var nameInput = api.root.getElementById('exp-name');
    var amountInput = api.root.getElementById('exp-amount');

    var name = nameInput.value.trim();
    var amount = parseFloat(amountInput.value);

    if (name && !isNaN(amount)) {
        expenses.unshift({
            name: name,
            amount: amount,
            date: new Date().toLocaleDateString()
        });
        nameInput.value = '';
        amountInput.value = '';
        render();
        api.saveState(expenses);
    }
}

function deleteExpense(idx) {
    expenses.splice(idx, 1);
    render();
    api.saveState(expenses);
}

function clearAll() {
    expenses = [];
    render();
    api.saveState(expenses);
}

async function init() {
    var saved = await api.getState();
    if (saved) expenses = saved;

    api.root.getElementById('btn-add').onclick = addExpense;
    api.root.getElementById('btn-clear').onclick = clearAll;
    api.root.getElementById('exp-name').onkeydown = function(e) { if (e.key === 'Enter') addExpense(); };

    render();
}

init();
