:host { display: block; font-family: var(--font-interface); }
* { box-sizing: border-box; }

.fitness-widget {
    background: var(--background-secondary);
    border: 1px solid var(--background-modifier-border);
    border-radius: 24px;
    padding: 1.5rem;
    color: var(--text-normal);
    box-shadow: var(--shadow-s);
    width: 100%;
    display: flex;
    flex-direction: column;
    gap: 1.5rem;
}

.widget-header {
    display: flex;
    flex-direction: column;
    gap: 1.25rem;
}

.header-main {
    display: flex;
    justify-content: space-between;
    align-items: center;
}

.widget-title {
    margin: 0;
    font-size: 0.85rem;
    font-weight: 700;
    text-transform: uppercase;
    letter-spacing: 0.1em;
    color: var(--text-muted);
}

.btn-icon {
    background: none;
    border: none;
    cursor: pointer;
    font-size: 1rem;
    opacity: 0.6;
    transition: opacity 0.2s;
}
.btn-icon:hover { opacity: 1; }

.input-container {
    background: rgba(0,0,0,0.1);
    padding: 1rem;
    border-radius: 16px;
    display: flex;
    flex-direction: column;
    gap: 10px;
}

.input-main {
    display: flex;
    gap: 8px;
}

.input-main input {
    flex: 1;
    background: var(--background-primary);
    border: 1px solid var(--background-modifier-border);
    color: var(--text-normal);
    padding: 10px 12px;
    border-radius: 10px;
    font-size: 0.85rem;
    outline: none;
}

.input-main select {
    background: var(--background-primary);
    border: 1px solid var(--background-modifier-border);
    color: var(--text-normal);
    padding: 0 8px;
    border-radius: 10px;
    font-size: 0.75rem;
    outline: none;
    cursor: pointer;
}

.input-details {
    display: flex;
    gap: 10px;
    align-items: flex-end;
}

.detail-field {
    flex: 1;
    display: flex;
    flex-direction: column;
    gap: 4px;
}

.detail-field label {
    font-size: 0.6rem;
    font-weight: 700;
    color: var(--text-muted);
    text-transform: uppercase;
    padding-left: 4px;
}

.detail-field input {
    background: var(--background-primary);
    border: 1px solid var(--background-modifier-border);
    color: var(--text-normal);
    padding: 8px;
    border-radius: 10px;
    font-size: 0.85rem;
    outline: none;
    width: 100%;
}

.btn-add {
    background: var(--interactive-accent);
    color: var(--text-on-accent);
    border: none;
    padding: 10px 16px;
    border-radius: 10px;
    font-size: 0.75rem;
    font-weight: 800;
    cursor: pointer;
    transition: all 0.2s;
    box-shadow: 0 4px 0 rgba(0,0,0,0.1);
}
.btn-add:hover { filter: brightness(1.1); transform: translateY(-1px); }
.btn-add:active { transform: translateY(1px); box-shadow: none; }

.history-section {
    display: flex;
    flex-direction: column;
    gap: 10px;
}

.history-header {
    display: flex;
    justify-content: space-between;
    align-items: center;
}

.history-header label {
    font-size: 0.65rem;
    font-weight: 700;
    color: var(--text-muted);
    text-transform: uppercase;
}

.history-count {
    font-size: 0.65rem;
    font-weight: 800;
    background: var(--background-modifier-border);
    padding: 2px 8px;
    border-radius: 10px;
    color: var(--text-muted);
}

.entry-list {
    display: flex;
    flex-direction: column;
    gap: 8px;
    max-height: 300px;
    overflow-y: auto;
    padding-right: 4px;
}
.entry-list::-webkit-scrollbar { width: 4px; }
.entry-list::-webkit-scrollbar-thumb { background: var(--background-modifier-border); border-radius: 4px; }

.entry-item {
    background: var(--background-primary);
    border: 1px solid var(--background-modifier-border);
    border-radius: 14px;
    padding: 12px;
    display: flex;
    justify-content: space-between;
    align-items: center;
    animation: slideIn 0.3s cubic-bezier(0.175, 0.885, 0.32, 1.275);
    position: relative;
    overflow: hidden;
}

@keyframes slideIn {
    from { opacity: 0; transform: translateX(-10px); }
    to { opacity: 1; transform: translateX(0); }
}

.entry-main {
    display: flex;
    align-items: center;
    gap: 12px;
}

.entry-icon {
    width: 36px;
    height: 36px;
    background: rgba(var(--interactive-accent-rgb), 0.1);
    border-radius: 10px;
    display: flex;
    align-items: center;
    justify-content: center;
    font-size: 1.2rem;
}

.entry-content {
    display: flex;
    flex-direction: column;
}

.entry-name {
    font-weight: 700;
    font-size: 0.85rem;
    color: var(--text-normal);
}

.entry-meta {
    font-size: 0.7rem;
    color: var(--text-muted);
    display: flex;
    gap: 8px;
    align-items: center;
}

.entry-time {
    font-size: 0.6rem;
    font-weight: 600;
    color: var(--interactive-accent);
    background: rgba(var(--interactive-accent-rgb), 0.05);
    padding: 1px 6px;
    border-radius: 4px;
}

.btn-del {
    background: none;
    border: none;
    color: var(--text-error);
    opacity: 0.3;
    cursor: pointer;
    padding: 8px;
    font-size: 1.1rem;
    transition: all 0.2s;
}
.btn-del:hover { opacity: 1; transform: scale(1.1); }

.footer-stats {
    display: flex;
    gap: 1rem;
    background: rgba(0,0,0,0.05);
    padding: 12px;
    border-radius: 16px;
}

.stat-item {
    flex: 1;
    display: flex;
    flex-direction: column;
    align-items: center;
}

.stat-val {
    font-size: 1.1rem;
    font-weight: 900;
    color: var(--interactive-accent);
}

.stat-label {
    font-size: 0.55rem;
    font-weight: 700;
    color: var(--text-muted);
    text-transform: uppercase;
    letter-spacing: 0.05em;
}

