const { root, saveState, getState } = api;

let entries = [];

const CATEGORY_ICONS = {
    strength: 'üèãÔ∏è',
    cardio: 'üèÉ',
    flexibility: 'üßò',
    other: '‚ú®'
};

function render() {
    const list = root.getElementById('entry-list');
    const count = root.getElementById('history-count');
    const totalStat = root.getElementById('stat-total');
    const todayStat = root.getElementById('stat-today');

    if (!list) return;
    list.innerHTML = '';

    const today = new Date().toLocaleDateString();
    let todayCount = 0;

    entries.forEach((entry, idx) => {
        const item = document.createElement('div');
        item.className = 'entry-item';

        const entryDate = new Date(entry.timestamp);
        const isToday = entryDate.toLocaleDateString() === today;
        if (isToday) todayCount++;

        const timeStr = entryDate.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' });
        const dateStr = isToday ? 'Today' : entryDate.toLocaleDateString([], { month: 'short', day: 'numeric' });

        item.innerHTML = `
            <div class="entry-main">
                <div class="entry-icon">${CATEGORY_ICONS[entry.category] || '‚ú®'}</div>
                <div class="entry-content">
                    <span class="entry-name">${entry.name}</span>
                    <div class="entry-meta">
                        <span>${entry.sets} sets √ó ${entry.reps}</span>
                        <span class="entry-time">${dateStr} ${timeStr}</span>
                    </div>
                </div>
            </div>
            <button class="btn-del" title="Delete Log">√ó</button>
        `;

        item.querySelector('.btn-del').onclick = () => deleteEntry(idx);
        list.appendChild(item);
    });

    if (count) count.innerText = `${entries.length} logs`;
    if (totalStat) totalStat.innerText = entries.length;
    if (todayStat) todayStat.innerText = todayCount;
}

function addEntry() {
    const nameIn = root.getElementById('fit-name');
    const categoryIn = root.getElementById('fit-category');
    const setsIn = root.getElementById('fit-sets');
    const repsIn = root.getElementById('fit-reps');

    const name = nameIn.value.trim();
    const category = categoryIn.value;
    const sets = setsIn.value || 0;
    const reps = repsIn.value || 0;

    if (name) {
        entries.unshift({
            name,
            category,
            sets,
            reps,
            timestamp: Date.now()
        });

        nameIn.value = '';
        setsIn.value = '';
        repsIn.value = '';

        render();
        save();
    }
}

function deleteEntry(idx) {
    entries.splice(idx, 1);
    render();
    save();
}

function clearAll() {
    if (confirm('Clear all fitness logs?')) {
        entries = [];
        render();
        save();
    }
}

function save() {
    saveState(entries);
}

async function init() {
    const saved = await getState();
    if (saved) entries = saved;

    root.getElementById('btn-add').onclick = addEntry;
    root.getElementById('btn-clear-all').onclick = clearAll;

    root.getElementById('fit-name').onkeydown = (e) => {
        if (e.key === 'Enter') addEntry();
    };

    render();
}

init();
