css:
:host { display: block; margin: 1.5rem 0; font-family: var(--font-interface); }
.widget-card {
    background: #111827;
    color: #f9fafb;
    border-radius: 16px;
    padding: 1.5rem;
    box-shadow: 0 10px 25px rgba(0,0,0,0.3);
    border: 1px solid #374151;
}
.widget-header { display: flex; justify-content: space-between; align-items: center; margin-bottom: 1.25rem; }
.widget-title { margin: 0; font-size: 1rem; font-weight: 800; color: #fbbf24; text-transform: uppercase; letter-spacing: 1px; }

.btn-add {
    background: #fbbf24;
    color: #111827;
    border: none;
    width: 28px;
    height: 28px;
    border-radius: 50%;
    font-weight: 800;
    cursor: pointer;
    transition: transform 0.2s;
}
.btn-add:hover { transform: scale(1.1); }

.input-row {
    display: grid;
    grid-template-columns: 2fr 1fr 1fr;
    gap: 8px;
    margin-bottom: 1.5rem;
}
.input-row input {
    background: #1f2937;
    border: 1px solid #374151;
    color: white;
    border-radius: 8px;
    padding: 8px;
    font-size: 0.8rem;
    outline: none;
}
.input-row input:focus { border-color: #fbbf24; }

.entry-list {
    max-height: 250px;
    overflow-y: auto;
    margin-bottom: 1rem;
    display: grid;
    gap: 10px;
}

.entry-item {
    background: #1f2937;
    border-radius: 10px;
    padding: 12px;
    display: flex;
    justify-content: space-between;
    align-items: center;
    border-left: 4px solid #fbbf24;
}

.entry-info { display: flex; flex-direction: column; }
.entry-name { font-weight: 700; font-size: 0.9rem; }
.entry-details { font-size: 0.75rem; color: #9ca3af; }

.entry-actions { display: flex; gap: 8px; }
.btn-del { background: none; border: none; color: #ef4444; cursor: pointer; font-size: 0.8rem; opacity: 0.6; }
.btn-del:hover { opacity: 1; }

.footer-stats { font-size: 0.75rem; color: #6b7280; text-align: center; font-weight: 600; }

js:
const { root, saveState, getState } = api;

let entries = [];

function render() {
    const list = root.getElementById('entry-list');
    const stats = root.getElementById('footer-stats');
    list.innerHTML = '';

    entries.forEach((entry, idx) => {
        const item = document.createElement('div');
        item.className = 'entry-item';
        item.innerHTML = `
            <div class="entry-info">
                <span class="entry-name">${entry.name}</span>
                <span class="entry-details">${entry.sets} sets Ã— ${entry.reps} reps</span>
            </div>
            <div class="entry-actions">
                <button class="btn-del" onclick="deleteEntry(${idx})">Delete</button>
            </div>
        `;
        list.appendChild(item);
    });

    stats.innerText = `Total Exercises: ${entries.length}`;
}

function addEntry() {
    const nameIn = root.getElementById('fit-name');
    const setsIn = root.getElementById('fit-sets');
    const repsIn = root.getElementById('fit-reps');

    const name = nameIn.value.trim();
    const sets = setsIn.value;
    const reps = repsIn.value;

    if (name && sets && reps) {
        entries.unshift({ name, sets, reps });
        nameIn.value = '';
        setsIn.value = '';
        repsIn.value = '';
        render();
        saveState(entries);
    }
}

function deleteEntry(idx) {
    entries.splice(idx, 1);
    render();
    saveState(entries);
}

async function init() {
    const saved = await getState();
    if (saved) entries = saved;
    render();
}

init();
