css:
:host { display: block; margin: 1.5rem 0; font-family: var(--font-interface); }
.widget-card {
    background: #1e1e2e; /* Dark theme for timer */
    color: #cdd6f4;
    border-radius: 16px;
    padding: 1.5rem;
    text-align: center;
    box-shadow: 0 8px 24px rgba(0,0,0,0.2);
}
.widget-title { color: #f38ba8; margin-bottom: 1rem; }
.timer-display { 
    font-size: 3.5rem; 
    font-weight: 800; 
    font-family: var(--font-monospace);
    margin: 0.5rem 0;
    color: #f38ba8;
}
.progress-bg { 
    background: #313244; 
    height: 8px; 
    border-radius: 4px; 
    margin: 1.5rem 0; 
    overflow: hidden;
}
.t-fill { 
    height: 100%; 
    width: 0%; 
    background: #f38ba8; 
    transition: width 1s linear; 
}
.controls { display: flex; justify-content: center; gap: 12px; }
.btn-timer {
    background: #f38ba8;
    color: #1e1e2e;
    border: none;
    padding: 10px 24px;
    border-radius: 10px;
    font-weight: 700;
    cursor: pointer;
    transition: all 0.2s;
}
.btn-timer:hover { transform: translateY(-2px); filter: brightness(1.1); }
.btn-secondary { background: #45475a; color: #cdd6f4; }

js:
const { root } = api;
let timeLeft = 25 * 60;
let totalTime = 25 * 60;
let timerId = null;

const display = root.getElementById('timer');
const bar = root.getElementById('t-progress');
const btnToggle = root.getElementById('btn-toggle');

function updateUI() {
    const mins = Math.floor(timeLeft / 60);
    const secs = timeLeft % 60;
    display.innerText = `${mins}:${secs < 10 ? '0' : ''}${secs}`;
    bar.style.width = ((totalTime - timeLeft) / totalTime * 100) + "%";
}

toggleTimer = () => {
    if (timerId) {
        clearInterval(timerId);
        timerId = null;
        btnToggle.innerText = "Reprendre";
    } else {
        btnToggle.innerText = "Pause";
        timerId = setInterval(() => {
            timeLeft--;
            updateUI();
            if (timeLeft <= 0) {
                clearInterval(timerId);
                timerId = null;
                btnToggle.innerText = "Terminé";
                new Audio('https://actions.google.com/sounds/v1/alarms/beep_short.ogg').play();
            }
        }, 1000);
    }
};

resetTimer = () => {
    clearInterval(timerId);
    timerId = null;
    timeLeft = 25 * 60;
    btnToggle.innerText = "Démarrer";
    updateUI();
};

updateUI();
