css:
:host { display: block; font-family: var(--font-interface); }
* { box-sizing: border-box; }

.github-widget {
    background: var(--background-secondary);
    border: 1px solid var(--background-modifier-border);
    border-radius: 24px;
    padding: 1.5rem;
    color: var(--text-normal);
    box-shadow: var(--shadow-s);
    width: 100%;
    display: flex;
    flex-direction: column;
    gap: 1.25rem;
    position: relative;
}

.widget-header {
    display: flex;
    flex-direction: column;
    gap: 1rem;
}

.header-main {
    display: flex;
    justify-content: space-between;
    align-items: center;
}

.widget-title {
    margin: 0;
    font-size: 0.8rem;
    font-weight: 700;
    text-transform: uppercase;
    letter-spacing: 0.1em;
    color: var(--text-muted);
}

.input-group {
    display: flex;
    gap: 8px;
}

.input-group input {
    flex: 1;
    background: var(--background-primary);
    border: 1px solid var(--background-modifier-border);
    color: var(--text-normal);
    padding: 10px 14px;
    border-radius: 12px;
    font-size: 0.85rem;
    outline: none;
    transition: border-color 0.2s;
}
.input-group input:focus { border-color: var(--interactive-accent); }

.input-group button {
    background: var(--interactive-accent);
    color: var(--text-on-accent);
    border: none;
    padding: 0 16px;
    border-radius: 12px;
    font-size: 0.75rem;
    font-weight: 800;
    cursor: pointer;
    transition: all 0.2s;
}
.input-group button:hover { filter: brightness(1.1); }

.profile-card {
    background: var(--background-primary);
    border: 1px solid var(--background-modifier-border);
    border-radius: 18px;
    padding: 1.25rem;
    display: flex;
    flex-direction: column;
    gap: 1.25rem;
    animation: fadeIn 0.3s ease;
}

@keyframes fadeIn {
    from { opacity: 0; transform: translateY(10px); }
    to { opacity: 1; transform: translateY(0); }
}

.profile-header {
    display: flex;
    gap: 16px;
    align-items: center;
}

.avatar {
    width: 60px;
    height: 60px;
    border-radius: 50%;
    border: 2px solid var(--interactive-accent);
    background: var(--background-secondary);
}

.user-info h2 {
    margin: 0;
    font-size: 1.1rem;
    font-weight: 800;
    line-height: 1.2;
}

.user-info p {
    margin: 2px 0;
    font-size: 0.8rem;
    color: var(--text-muted);
}

.bio {
    font-size: 0.8rem !important;
    line-height: 1.4;
    margin-top: 6px !important;
    color: var(--text-normal) !important;
}

.stats-grid {
    display: grid;
    grid-template-columns: repeat(3, 1fr);
    gap: 10px;
}

.stat-item {
    background: var(--background-secondary);
    padding: 10px;
    border-radius: 12px;
    text-align: center;
    display: flex;
    flex-direction: column;
    gap: 2px;
}

.stat-val {
    font-size: 1.1rem;
    font-weight: 800;
    color: var(--interactive-accent);
}

.stat-label {
    font-size: 0.6rem;
    font-weight: 700;
    text-transform: uppercase;
    color: var(--text-muted);
}

.meta-info {
    display: flex;
    flex-direction: column;
    gap: 6px;
    padding-top: 10px;
    border-top: 1px solid var(--background-modifier-border);
}

.meta-item {
    display: flex;
    align-items: center;
    gap: 8px;
    font-size: 0.75rem;
    color: var(--text-muted);
}

.meta-item a {
    color: var(--interactive-accent);
    text-decoration: none;
}
.meta-item a:hover { text-decoration: underline; }

.loading-state, .error-state, .empty-state {
    padding: 2rem;
    text-align: center;
    display: flex;
    flex-direction: column;
    align-items: center;
    gap: 12px;
    color: var(--text-muted);
    font-size: 0.85rem;
}

.spinner {
    width: 24px;
    height: 24px;
    border: 3px solid var(--background-modifier-border);
    border-top-color: var(--interactive-accent);
    border-radius: 50%;
    animation: spin 1s linear infinite;
}

@keyframes spin {
    to { transform: rotate(360deg); }
}

.empty-icon { font-size: 2rem; opacity: 0.5; }
.hidden { display: none !important; }

.btn-icon {
    background: none;
    border: none;
    cursor: pointer;
    font-size: 1rem;
    opacity: 0.6;
    transition: all 0.2s;
}
.btn-icon:hover { opacity: 1; transform: rotate(180deg); }

js:
const { root, saveState, getState } = api;

let state = {
    lastUsername: ""
};

async function fetchGitHubData(username) {
    if (!username) return;

    showState('loading');

    try {
        const response = await fetch(`https://api.github.com/users/${username}`);
        if (!response.ok) throw new Error('User not found');

        const data = await response.json();
        updateProfile(data);
        state.lastUsername = username;
        saveState(state);
        showState('profile');
    } catch (err) {
        console.error(err);
        showState('error');
    }
}

function updateProfile(data) {
    root.getElementById('avatar-img').src = data.avatar_url;
    root.getElementById('user-name').innerText = data.name || data.login;
    root.getElementById('user-login').innerText = `@${data.login}`;
    root.getElementById('user-bio').innerText = data.bio || "No bio available";

    root.getElementById('stat-repos').innerText = data.public_repos;
    root.getElementById('stat-followers').innerText = data.followers;
    root.getElementById('stat-following').innerText = data.following;

    const location = root.getElementById('meta-location');
    if (data.location) {
        location.classList.remove('hidden');
        root.getElementById('val-location').innerText = data.location;
    } else {
        location.classList.add('hidden');
    }

    const blog = root.getElementById('meta-blog');
    if (data.blog) {
        blog.classList.remove('hidden');
        const blogLink = root.getElementById('val-blog');
        blogLink.href = data.blog.startsWith('http') ? data.blog : `https://${data.blog}`;
        blogLink.innerText = data.blog.replace(/^https?:\/\//, '');
    } else {
        blog.classList.add('hidden');
    }
}

function showState(mode) {
    const states = {
        profile: root.getElementById('profile-card'),
        loading: root.getElementById('loading-state'),
        error: root.getElementById('error-state'),
        empty: root.getElementById('empty-state')
    };

    Object.keys(states).forEach(key => {
        if (states[key]) {
            if (key === mode) states[key].classList.remove('hidden');
            else states[key].classList.add('hidden');
        }
    });
}

async function init() {
    const saved = await getState();
    if (saved && saved.lastUsername) {
        state = saved;
        root.getElementById('gh-username').value = state.lastUsername;
        fetchGitHubData(state.lastUsername);
    }

    root.getElementById('btn-fetch').onclick = () => {
        const user = root.getElementById('gh-username').value.trim();
        fetchGitHubData(user);
    };

    root.getElementById('gh-username').onkeydown = (e) => {
        if (e.key === 'Enter') {
            const user = root.getElementById('gh-username').value.trim();
            fetchGitHubData(user);
        }
    };

    root.getElementById('btn-refresh').onclick = () => {
        const user = root.getElementById('gh-username').value.trim();
        if (user) fetchGitHubData(user);
    };
}

init();
