css:
:host { display: block; margin: 1.5rem 0; font-family: var(--font-interface); }
.widget-card {
    background: #0d1117;
    color: #c9d1d9;
    border: 1px solid #30363d;
    border-radius: 12px;
    padding: 1.5rem;
    box-shadow: 0 8px 24px rgba(0,0,0,0.2);
}

.profile-header {
    display: flex;
    align-items: center;
    gap: 16px;
    margin-bottom: 1.5rem;
}

.avatar {
    width: 64px;
    height: 64px;
    background: #21262d;
    border: 1px solid #30363d;
    border-radius: 50%;
    display: flex;
    align-items: center;
    justify-content: center;
    font-weight: 800;
    font-size: 1.2rem;
    color: #58a6ff;
}

.user-info h4 { margin: 0; font-size: 1.1rem; color: #58a6ff; }
.user-info p { margin: 4px 0 0 0; font-size: 0.85rem; color: #8b949e; }

.stats-grid {
    display: grid;
    grid-template-columns: repeat(3, 1fr);
    gap: 12px;
    margin-bottom: 1.5rem;
    text-align: center;
}

.stat-item {
    background: #161b22;
    border: 1px solid #30363d;
    border-radius: 8px;
    padding: 10px;
}

.stat-val { display: block; font-weight: 700; font-size: 1.1rem; color: #f0f6fc; }
.stat-label { font-size: 0.7rem; color: #8b949e; text-transform: uppercase; }

.input-area {
    display: flex;
    gap: 8px;
}

.input-area input {
    flex: 1;
    background: #0d1117;
    border: 1px solid #30363d;
    color: #c9d1d9;
    border-radius: 6px;
    padding: 6px 12px;
    font-size: 0.85rem;
    outline: none;
}
.input-area input:focus { border-color: #58a6ff; }

.input-area button {
    background: #238636;
    color: white;
    border: none;
    border-radius: 6px;
    padding: 6px 16px;
    font-weight: 600;
    cursor: pointer;
}
.input-area button:hover { background: #2ea043; }

js:
const { root, saveState, getState } = api;

let state = {
    username: "GitHub User",
    bio: "Building the future...",
    repos: 12,
    followers: 450,
    following: 120
};

async function init() {
    const saved = await getState();
    if (saved) state = saved;
    updateUI();
}

function updateUI() {
    root.getElementById('username').innerText = state.username;
    root.getElementById('bio').innerText = state.bio;
    root.getElementById('repos').innerText = state.repos;
    root.getElementById('followers').innerText = state.followers;
    root.getElementById('following').innerText = state.following;
    root.getElementById('avatar').innerText = state.username.substring(0, 2).toUpperCase();
}

fetchUser = async () => {
    const user = root.getElementById('gh-username').value.trim();
    if (!user) return;

    // Simulate API fetch
    state.username = user;
    state.bio = "Full-stack developer & open source enthusiast.";
    state.repos = Math.floor(Math.random() * 100);
    state.followers = Math.floor(Math.random() * 1000);
    state.following = Math.floor(Math.random() * 500);

    updateUI();
    await saveState(state);
};

init();
