css:
:host { display: block; margin: 1.5rem 0; font-family: var(--font-interface); }
.widget-card {
    background: #1e293b;
    color: #f8fafc;
    border-radius: 20px;
    padding: 1.5rem;
    box-shadow: 0 10px 25px rgba(0,0,0,0.2);
    border: 1px solid #334155;
}
.widget-header { display: flex; justify-content: space-between; align-items: center; margin-bottom: 1.5rem; }
.widget-title { margin: 0; font-size: 1rem; font-weight: 800; color: #38bdf8; text-transform: uppercase; letter-spacing: 1px; }

.header-actions { display: flex; gap: 6px; }

.btn-icon {
    background: #334155;
    color: #94a3b8;
    border: none;
    width: 28px;
    height: 28px;
    border-radius: 50%;
    cursor: pointer;
    display: flex;
    align-items: center;
    justify-content: center;
    font-weight: bold;
    transition: all 0.2s;
    font-size: 0.85rem;
}
.btn-icon:hover { background: #475569; color: white; }

.btn-add {
    background: #38bdf8;
    color: #0f172a;
    border: none;
    width: 28px;
    height: 28px;
    border-radius: 50%;
    font-weight: 800;
    cursor: pointer;
    transition: transform 0.2s;
    display: flex;
    align-items: center;
    justify-content: center;
}
.btn-add:hover { transform: scale(1.1); }

.habit-list { display: grid; gap: 16px; margin-bottom: 1.5rem; }

.habit-item {
    background: rgba(255,255,255,0.03);
    border-radius: 12px;
    padding: 12px;
    border: 1px solid rgba(255,255,255,0.05);
    transition: all 0.2s;
    position: relative;
}
.habit-item:hover { background: rgba(255,255,255,0.05); }
.habit-item:hover .btn-delete-habit { opacity: 1; }

.habit-info { display: flex; justify-content: space-between; align-items: center; margin-bottom: 8px; }

.habit-name-wrapper { display: flex; align-items: center; gap: 8px; flex: 1; }

.habit-name-input {
    background: transparent;
    border: none;
    color: #f8fafc;
    font-weight: 700;
    font-size: 0.9rem;
    outline: none;
    padding: 4px;
    border-radius: 4px;
    width: 100%;
}
.habit-name-input:focus { background: rgba(255,255,255,0.05); }

.habit-streak { font-size: 0.75rem; color: #94a3b8; font-weight: 600; white-space: nowrap; }

.btn-delete-habit {
    background: none;
    border: none;
    color: #ef4444;
    cursor: pointer;
    font-size: 0.9rem;
    opacity: 0;
    transition: opacity 0.2s;
    padding: 4px;
    margin-left: 8px;
}
.btn-delete-habit:hover { opacity: 1 !important; }

.habit-grid {
    display: grid;
    grid-template-columns: repeat(7, 1fr);
    gap: 6px;
}

.day-dot {
    aspect-ratio: 1;
    background: #334155;
    border-radius: 4px;
    cursor: pointer;
    transition: all 0.2s;
    display: flex;
    align-items: center;
    justify-content: center;
    font-size: 0.6rem;
    font-weight: 800;
    color: rgba(255,255,255,0.2);
}
.day-dot.done {
    background: #38bdf8;
    color: #0f172a;
    box-shadow: 0 0 8px rgba(56, 189, 248, 0.4);
}
.day-dot:hover { transform: scale(1.1); background: #475569; }
.day-dot.done:hover { background: #7dd3fc; }

.stats-footer {
    display: grid;
    grid-template-columns: 1fr 1fr;
    gap: 12px;
    border-top: 1px solid #334155;
    padding-top: 1.25rem;
}

.stat-box { text-align: center; }
.stat-label { display: block; font-size: 0.65rem; color: #94a3b8; text-transform: uppercase; font-weight: 700; margin-bottom: 4px; }
.stat-val { font-size: 1.1rem; font-weight: 800; color: #f8fafc; }
