css:
:host { display: block; margin: 1.5rem 0; font-family: var(--font-interface); }
.widget-card {
    background: #1e293b;
    color: #f8fafc;
    border-radius: 20px;
    padding: 1.5rem;
    box-shadow: 0 10px 25px rgba(0,0,0,0.3);
    border: 1px solid #334155;
    text-align: center;
}
.widget-title { margin: 0 0 1.5rem 0; font-size: 0.9rem; font-weight: 800; color: #38bdf8; text-transform: uppercase; letter-spacing: 2px; }

textarea {
    width: 100%;
    height: 150px;
    background: #0f172a;
    border: 1px solid #334155;
    color: #38bdf8;
    border-radius: 12px;
    padding: 12px;
    font-size: 0.8rem;
    outline: none;
    resize: vertical;
    font-family: var(--font-monospace);
    margin-bottom: 1rem;
}

.actions { display: flex; gap: 8px; }
.actions button {
    flex: 1;
    background: #334155;
    color: white;
    border: none;
    padding: 8px;
    border-radius: 8px;
    font-weight: 700;
    font-size: 0.75rem;
    cursor: pointer;
}
.actions button:hover { background: #475569; }

.error { color: #ef4444; font-size: 0.7rem; margin-top: 8px; font-weight: 700; }

js:
const { root, saveState, getState } = api;

function format() {
    const input = root.getElementById('json-input');
    const error = root.getElementById('error-msg');
    error.innerText = '';
    try {
        const obj = JSON.parse(input.value);
        input.value = JSON.stringify(obj, null, 2);
        saveState(input.value);
    } catch(e) {
        error.innerText = "Invalid JSON: " + e.message;
    }
}

function minify() {
    const input = root.getElementById('json-input');
    const error = root.getElementById('error-msg');
    error.innerText = '';
    try {
        const obj = JSON.parse(input.value);
        input.value = JSON.stringify(obj);
        saveState(input.value);
    } catch(e) {
        error.innerText = "Invalid JSON: " + e.message;
    }
}

function copy() {
    const input = root.getElementById('json-input');
    if (navigator.clipboard && navigator.clipboard.writeText) {
        navigator.clipboard.writeText(input.value);
    } else {
        input.select();
        document.execCommand('copy');
    }
}

async function init() {
    const saved = await getState();
    if (saved) root.getElementById('json-input').value = saved;
}

init();
