const { root, saveState, getState } = api;
const area = root.getElementById('json-input');
const status = root.getElementById('json-status');

function transform(type) {
    try {
        const obj = JSON.parse(area.value);
        area.value = type === 'pretty' ? JSON.stringify(obj, null, 2) : JSON.stringify(obj);
        status.innerText = "âœ… JSON Valide";
        status.style.color = "#98c379";
        saveState(area.value);
    } catch (e) {
        status.innerText = "âŒ Erreur de syntaxe";
        status.style.color = "#e06c75";
    }
}

function copyJSON() {
    if (navigator.clipboard && navigator.clipboard.writeText) {
        navigator.clipboard.writeText(area.value);
    }
    status.innerText = "ğŸ“‹ CopiÃ© !";
    setTimeout(() => { status.innerText = "PrÃªt"; status.style.color = "#5c6370"; }, 2000);
}

async function init() {
    const saved = await getState();
    if (typeof saved === 'string') {
        area.value = saved;
        transform('pretty');
    }
}

init();
