css:
:host { display: block; font-family: var(--font-interface); }
* { box-sizing: border-box; }

.lingo-widget {
    background: var(--background-secondary);
    border: 1px solid var(--background-modifier-border);
    border-radius: 24px;
    padding: 1.5rem;
    color: var(--text-normal);
    box-shadow: var(--shadow-s);
    width: 100%;
    display: flex;
    flex-direction: column;
    gap: 1.25rem;
    position: relative;
    overflow: hidden;
}

.widget-header {
    display: flex;
    flex-direction: column;
    gap: 0.75rem;
}

.header-main {
    display: flex;
    justify-content: space-between;
    align-items: center;
}

.widget-title {
    margin: 0;
    font-size: 0.8rem;
    font-weight: 700;
    text-transform: uppercase;
    letter-spacing: 0.1em;
    color: var(--text-muted);
}

.btn-icon {
    background: var(--background-primary);
    border: 1px solid var(--background-modifier-border);
    border-radius: 8px;
    width: 32px;
    height: 32px;
    display: flex;
    align-items: center;
    justify-content: center;
    cursor: pointer;
    transition: all 0.2s;
}
.btn-icon:hover { background: var(--background-modifier-hover); }
.btn-icon.active { background: var(--interactive-accent); color: var(--text-on-accent); }

.progress-bar {
    height: 4px;
    background: var(--background-modifier-border);
    border-radius: 2px;
    overflow: hidden;
}

.progress-fill {
    height: 100%;
    background: var(--interactive-accent);
    width: 50%;
    transition: width 0.3s ease;
}

.card-viewport {
    perspective: 1000px;
    width: 100%;
    height: 180px;
    cursor: pointer;
}

.flashcard {
    width: 100%;
    height: 100%;
    position: relative;
    transition: transform 0.6s cubic-bezier(0.4, 0, 0.2, 1);
    transform-style: preserve-3d;
}

.flashcard.flipped { transform: rotateY(180deg); }

.card-face {
    position: absolute;
    width: 100%;
    height: 100%;
    backface-visibility: hidden;
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    background: var(--background-primary);
    border: 1px solid var(--background-modifier-border);
    border-radius: 20px;
    box-shadow: var(--shadow-s);
    padding: 20px;
}

.card-face.back {
    transform: rotateY(180deg);
    background: var(--background-primary-alt);
}

.card-content {
    font-size: 1.75rem;
    font-weight: 800;
    text-align: center;
    color: var(--text-normal);
    word-break: break-word;
}

.card-label {
    position: absolute;
    bottom: 12px;
    font-size: 0.6rem;
    font-weight: 800;
    color: var(--text-muted);
    text-transform: uppercase;
    letter-spacing: 0.1em;
}

.controls-main {
    display: flex;
    justify-content: space-between;
    align-items: center;
    gap: 15px;
}

.btn-nav {
    background: var(--background-primary);
    border: 1px solid var(--background-modifier-border);
    color: var(--text-normal);
    width: 44px;
    height: 44px;
    border-radius: 12px;
    font-size: 1.2rem;
    cursor: pointer;
    transition: all 0.2s;
}
.btn-nav:hover { background: var(--background-modifier-hover); transform: scale(1.05); }

.card-counter {
    font-size: 0.85rem;
    font-weight: 700;
    color: var(--text-muted);
}

.edit-panel {
    background: rgba(0,0,0,0.05);
    padding: 15px;
    border-radius: 16px;
    animation: slideDown 0.3s ease;
}

@keyframes slideDown {
    from { opacity: 0; transform: translateY(-10px); }
    to { opacity: 1; transform: translateY(0); }
}

.input-group {
    display: flex;
    flex-direction: column;
    gap: 10px;
}

.input-group input {
    background: var(--background-primary);
    border: 1px solid var(--background-modifier-border);
    color: var(--text-normal);
    padding: 10px 14px;
    border-radius: 10px;
    font-size: 0.85rem;
    outline: none;
}

.edit-actions {
    display: flex;
    gap: 8px;
}

.btn-save {
    flex: 2;
    background: var(--interactive-accent);
    color: var(--text-on-accent);
    border: none;
    padding: 10px;
    border-radius: 10px;
    font-weight: 800;
    font-size: 0.75rem;
    cursor: pointer;
}

.btn-delete {
    flex: 1;
    background: var(--background-modifier-error);
    color: white;
    border: none;
    padding: 10px;
    border-radius: 10px;
    font-weight: 800;
    font-size: 0.75rem;
    cursor: pointer;
}

.toast {
    position: absolute;
    bottom: 20px;
    left: 50%;
    transform: translateX(-50%) translateY(100px);
    background: var(--interactive-accent);
    color: var(--text-on-accent);
    padding: 8px 16px;
    border-radius: 20px;
    font-size: 0.8rem;
    font-weight: 600;
    transition: transform 0.3s cubic-bezier(0.175, 0.885, 0.32, 1.275);
    z-index: 100;
}
.toast.show { transform: translateX(-50%) translateY(0); }

.hidden { display: none !important; }

js:
const { root, saveState, getState } = api;

let state = {
    cards: [
        { f: 'Bonjour', b: 'Hello' },
        { f: 'Merci', b: 'Thank you' }
    ],
    currentIndex: 0,
    isEditing: false
};

function render() {
    const frontEl = root.getElementById('card-front');
    const backEl = root.getElementById('card-back');
    const statsEl = root.getElementById('card-stats');
    const progressFill = root.getElementById('progress-fill');
    const flashcard = root.getElementById('flashcard');
    const editPanel = root.getElementById('edit-panel');
    const editBtn = root.getElementById('btn-edit-toggle');

    if (state.isEditing) {
        editPanel.classList.remove('hidden');
        editBtn.classList.add('active');
        // Fill inputs with current card data for easy editing
        if (state.cards.length > 0) {
            root.getElementById('input-front').value = state.cards[state.currentIndex].f;
            root.getElementById('input-back').value = state.cards[state.currentIndex].b;
        } else {
            root.getElementById('input-front').value = '';
            root.getElementById('input-back').value = '';
        }
    } else {
        editPanel.classList.add('hidden');
        editBtn.classList.remove('active');
    }

    if (state.cards.length === 0) {
        frontEl.innerText = 'No cards';
        backEl.innerText = 'Add one in settings!';
        statsEl.innerText = '0 / 0';
        progressFill.style.width = '0%';
        return;
    }

    const card = state.cards[state.currentIndex];
    frontEl.innerText = card.f;
    backEl.innerText = card.b;
    flashcard.classList.remove('flipped');

    statsEl.innerText = `${state.currentIndex + 1} / ${state.cards.length}`;
    const progress = ((state.currentIndex + 1) / state.cards.length) * 100;
    progressFill.style.width = `${progress}%`;
}

function flipCard() {
    root.getElementById('flashcard').classList.toggle('flipped');
}

function nextCard() {
    if (state.cards.length === 0) return;
    state.currentIndex = (state.currentIndex + 1) % state.cards.length;
    render();
}

function prevCard() {
    if (state.cards.length === 0) return;
    state.currentIndex = (state.currentIndex - 1 + state.cards.length) % state.cards.length;
    render();
}

function saveOrUpdateCard() {
    const fInput = root.getElementById('input-front');
    const bInput = root.getElementById('input-back');
    const f = fInput.value.trim();
    const b = bInput.value.trim();

    if (!f || !b) return;

    // If we are editing an existing card's index, we might want to update it
    // But for simplicity in a widget, let's treat it as "Add New" if inputs were empty or "Update" if they match current
    // Actually, let's just make it a simple "Add" button and use the inputs.
    // If the user wants to edit, they change the text and we can have a logic for that.

    // Logic: If current card exists and inputs are different, update it. Otherwise add new.
    if (state.cards.length > 0 && (f !== state.cards[state.currentIndex].f || b !== state.cards[state.currentIndex].b)) {
        // Update existing
        state.cards[state.currentIndex] = { f, b };
        showToast('Card updated! ‚ú®');
    } else {
        // Add new
        state.cards.push({ f, b });
        state.currentIndex = state.cards.length - 1;
        showToast('Card added! üöÄ');
    }

    save();
    render();
}

function deleteCard() {
    if (state.cards.length === 0) return;
    state.cards.splice(state.currentIndex, 1);
    if (state.currentIndex >= state.cards.length) {
        state.currentIndex = Math.max(0, state.cards.length - 1);
    }
    save();
    render();
    showToast('Deleted! üóëÔ∏è');
}

function toggleEdit() {
    state.isEditing = !state.isEditing;
    render();
}

function showToast(msg) {
    const toast = root.getElementById('toast');
    if (toast) {
        toast.innerText = msg;
        toast.classList.add('show');
        setTimeout(() => toast.classList.remove('show'), 2000);
    }
}

function save() {
    saveState(state);
}

async function init() {
    const saved = await getState();
    if (saved) {
        if (Array.isArray(saved)) state.cards = saved;
        else Object.assign(state, saved);
    }

    root.getElementById('flashcard').onclick = flipCard;
    root.getElementById('btn-prev').onclick = prevCard;
    root.getElementById('btn-next').onclick = nextCard;
    root.getElementById('btn-edit-toggle').onclick = toggleEdit;
    root.getElementById('btn-save-card').onclick = saveOrUpdateCard;
    root.getElementById('btn-del-card').onclick = deleteCard;

    render();
}

init();
