css:
:host { display: block; margin: 1.5rem 0; font-family: var(--font-interface); }
.widget-card {
    background: #fdf4ff;
    color: #701a75;
    border-radius: 20px;
    padding: 1.5rem;
    box-shadow: 0 10px 25px rgba(0,0,0,0.05);
    border: 1px solid #f5d0fe;
    text-align: center;
}
.widget-title { margin: 0 0 1.5rem 0; font-size: 0.9rem; font-weight: 800; color: #a21caf; text-transform: uppercase; letter-spacing: 1px; }

.card-container {
    perspective: 1000px;
    width: 100%;
    height: 120px;
    margin-bottom: 1.5rem;
    cursor: pointer;
}

.flashcard {
    width: 100%;
    height: 100%;
    position: relative;
    transition: transform 0.6s;
    transform-style: preserve-3d;
}
.flashcard.flipped { transform: rotateY(180deg); }

.front, .back {
    position: absolute;
    width: 100%;
    height: 100%;
    backface-visibility: hidden;
    display: flex;
    align-items: center;
    justify-content: center;
    font-size: 1.5rem;
    font-weight: 800;
    background: white;
    border-radius: 16px;
    border: 2px solid #f5d0fe;
    box-shadow: 0 4px 10px rgba(0,0,0,0.02);
}

.back {
    transform: rotateY(180deg);
    background: #fae8ff;
    color: #a21caf;
}

.controls {
    display: flex;
    justify-content: center;
    gap: 12px;
    margin-bottom: 1rem;
}
.controls button {
    background: white;
    border: 1px solid #f5d0fe;
    color: #a21caf;
    width: 40px;
    height: 40px;
    border-radius: 10px;
    font-weight: 800;
    cursor: pointer;
}
.btn-add { background: #a21caf !important; color: white !important; }
.btn-del { background: #ef4444 !important; color: white !important; border-color: #ef4444 !important; }

.stats { font-size: 0.75rem; font-weight: 700; opacity: 0.6; }

js:
var cards = [
    { f: 'Bonjour', b: 'Hello' },
    { f: 'Merci', b: 'Thank you' }
];
var currentIndex = 0;

function render() {
    if (cards.length === 0) {
        api.root.getElementById('card-front').innerText = 'No cards';
        api.root.getElementById('card-back').innerText = 'Add one!';
        api.root.getElementById('card-stats').innerText = '0 cards';
        return;
    }
    var card = cards[currentIndex];
    api.root.getElementById('card-front').innerText = card.f;
    api.root.getElementById('card-back').innerText = card.b;
    api.root.getElementById('flashcard').classList.remove('flipped');
    api.root.getElementById('card-stats').innerText = 'Card ' + (currentIndex + 1) + ' of ' + cards.length;
}

function flipCard() {
    api.root.getElementById('flashcard').classList.toggle('flipped');
}

function nextCard() {
    if (cards.length === 0) return;
    currentIndex = (currentIndex + 1) % cards.length;
    render();
}

function prevCard() {
    if (cards.length === 0) return;
    currentIndex = (currentIndex - 1 + cards.length) % cards.length;
    render();
}

function addCard() {
    var front = prompt('Front (word to learn):', '');
    if (front) {
        var back = prompt('Back (translation):', '');
        if (back) {
            cards.push({ f: front, b: back });
            currentIndex = cards.length - 1;
            render();
            api.saveState(cards);
        }
    }
}

function deleteCard() {
    if (cards.length === 0) return;
    cards.splice(currentIndex, 1);
    if (currentIndex >= cards.length) currentIndex = Math.max(0, cards.length - 1);
    render();
    api.saveState(cards);
}

async function init() {
    var saved = await api.getState();
    if (saved) cards = saved;

    api.root.getElementById('card-container').onclick = flipCard;
    api.root.getElementById('btn-prev').onclick = prevCard;
    api.root.getElementById('btn-next').onclick = nextCard;
    api.root.getElementById('btn-add').onclick = addCard;
    api.root.getElementById('btn-del').onclick = deleteCard;

    render();
}

init();
