css:
:host { display: block; margin: 1.5rem 0; font-family: var(--font-interface); }
.widget-card {
    background: #1e1e2e;
    color: #cdd6f4;
    border-radius: 16px;
    padding: 1.25rem;
    box-shadow: 0 8px 20px rgba(0,0,0,0.2);
    border: 1px solid #313244;
    text-align: center;
}
.widget-title { margin: 0 0 1.25rem 0; font-size: 0.85rem; font-weight: 800; color: #89b4fa; text-transform: uppercase; letter-spacing: 1px; }

.section { margin-bottom: 1.25rem; }
label { display: block; font-size: 0.65rem; font-weight: 700; color: #9399b2; text-transform: uppercase; margin-bottom: 8px; }

.moods { display: flex; justify-content: center; gap: 12px; }
.moods span {
    font-size: 1.5rem;
    cursor: pointer;
    transition: transform 0.2s;
    filter: grayscale(100%);
    opacity: 0.5;
}
.moods span:hover { transform: scale(1.2); opacity: 1; }
.moods span.active { filter: grayscale(0%); opacity: 1; transform: scale(1.2); }

.energy-meter { display: flex; gap: 4px; justify-content: center; }
.level {
    width: 30px;
    height: 8px;
    background: #313244;
    border-radius: 4px;
    cursor: pointer;
}
.level.active { background: #fab387; }

.history { 
    font-size: 0.75rem; 
    color: #9399b2; 
    background: #181825; 
    padding: 8px; 
    border-radius: 8px; 
    margin-top: 1rem;
    font-style: italic;
}

.btn-reset {
    background: none;
    border: none;
    color: #f38ba8;
    font-size: 0.7rem;
    margin-top: 12px;
    cursor: pointer;
    text-decoration: underline;
    opacity: 0.7;
}
.btn-reset:hover { opacity: 1; }

js:
var state = { mood: '', energy: 0 };

function setMood(m) {
    state.mood = m;
    updateUI();
    api.saveState(state);
}

function setEnergy(e) {
    state.energy = e;
    updateUI();
    api.saveState(state);
}

function updateUI() {
    api.root.querySelectorAll('.moods span').forEach(function(s) {
        s.classList.toggle('active', s.getAttribute('data-val') === state.mood);
    });
    api.root.querySelectorAll('.level').forEach(function(l) {
        l.classList.toggle('active', parseInt(l.getAttribute('data-val')) <= state.energy);
    });

    var hist = api.root.getElementById('history');
    if (state.mood || state.energy) {
        hist.innerText = 'Last entry: ' + (state.mood || '?') + ' | Energy: ' + (state.energy || '?') + '/5';
    } else {
        hist.innerText = 'No data yet';
    }
}

function reset() {
    state = { mood: '', energy: 0 };
    updateUI();
    api.saveState(state);
}

async function init() {
    var saved = await api.getState();
    if (saved) state = saved;

    api.root.querySelectorAll('.moods span').forEach(function(s) {
        s.onclick = function() { setMood(this.getAttribute('data-val')); };
    });

    api.root.querySelectorAll('.level').forEach(function(l) {
        l.onclick = function() { setEnergy(parseInt(this.getAttribute('data-val'))); };
    });

    api.root.getElementById('btn-reset').onclick = reset;

    updateUI();
}

init();
