css:
:host { display: block; margin: 1.5rem 0; font-family: var(--font-interface); }
.widget-card {
    background: linear-gradient(135deg, #6366f1 0%, #a855f7 100%);
    color: white;
    border-radius: 24px;
    padding: 1.5rem;
    box-shadow: 0 10px 30px rgba(99, 102, 241, 0.3);
    text-align: center;
}
.widget-title { margin: 0 0 1.5rem 0; font-size: 0.9rem; font-weight: 800; text-transform: uppercase; letter-spacing: 2px; opacity: 0.9; }

.mood-selector {
    display: flex;
    justify-content: space-between;
    background: rgba(255,255,255,0.15);
    padding: 12px;
    border-radius: 100px;
    margin-bottom: 2rem;
    backdrop-filter: blur(10px);
}

.mood-btn {
    background: none;
    border: none;
    font-size: 1.75rem;
    cursor: pointer;
    transition: transform 0.2s;
}
.mood-btn:hover { transform: scale(1.3); }

.history-section { text-align: left; margin-bottom: 1.5rem; }
.section-header { font-size: 0.75rem; font-weight: 700; text-transform: uppercase; opacity: 0.7; margin-bottom: 10px; }

.mood-history {
    display: flex;
    gap: 8px;
    overflow-x: auto;
    padding-bottom: 8px;
}
.mood-history::-webkit-scrollbar { height: 4px; }
.mood-history::-webkit-scrollbar-thumb { background: rgba(255,255,255,0.3); border-radius: 10px; }

.history-item {
    background: rgba(255,255,255,0.1);
    padding: 8px;
    border-radius: 12px;
    min-width: 45px;
    text-align: center;
    display: flex;
    flex-direction: column;
    align-items: center;
}
.history-emoji { font-size: 1.25rem; }
.history-date { font-size: 0.6rem; font-weight: 700; margin-top: 4px; opacity: 0.8; }

.mood-stats {
    display: grid;
    grid-template-columns: 1fr 1fr;
    gap: 12px;
    background: rgba(0,0,0,0.1);
    padding: 12px;
    border-radius: 16px;
}

.stat span { display: block; font-size: 1.25rem; font-weight: 800; }
.stat label { font-size: 0.65rem; font-weight: 700; text-transform: uppercase; opacity: 0.7; }

js:
const { root, saveState, getState } = api;

let history = [];

async function init() {
    const saved = await getState();
    if (saved) history = saved;
    render();
}

function render() {
    const list = root.getElementById('mood-history');
    list.innerHTML = '';

    const moodCounts = {};
    history.forEach(item => {
        const el = document.createElement('div');
        el.className = 'history-item';
        el.innerHTML = `
            <span class="history-emoji">${item.emoji}</span>
            <span class="history-date">${item.date}</span>
        `;
        list.appendChild(el);

        moodCounts[item.emoji] = (moodCounts[item.emoji] || 0) + 1;
    });

    root.getElementById('mood-count').innerText = history.length;

    let topMood = '-';
    let max = 0;
    for (const m in moodCounts) {
        if (moodCounts[m] > max) {
            max = moodCounts[m];
            topMood = m;
        }
    }
    root.getElementById('top-mood').innerText = topMood;
}

logMood = async (emoji, label) => {
    const date = new Date().toLocaleDateString('en-US', { month: 'short', day: 'numeric' });
    history.unshift({ emoji, label, date });
    if (history.length > 10) history.pop();

    render();
    await saveState(history);
};

init();
