css:
:host { display: block; margin: 1.5rem 0; font-family: var(--font-interface); }
.widget-card {
    background: linear-gradient(135deg, #6366f1 0%, #a855f7 100%);
    color: white;
    border-radius: 24px;
    padding: 1.5rem;
    box-shadow: 0 10px 30px rgba(99, 102, 241, 0.3);
    text-align: center;
}
.widget-title { margin: 0 0 1.5rem 0; font-size: 0.9rem; font-weight: 800; text-transform: uppercase; letter-spacing: 2px; opacity: 0.9; }

.mood-selector {
    display: flex;
    justify-content: space-between;
    background: rgba(255,255,255,0.15);
    padding: 12px;
    border-radius: 100px;
    margin-bottom: 2rem;
    backdrop-filter: blur(10px);
}

.mood-btn {
    background: none;
    border: none;
    font-size: 1.75rem;
    cursor: pointer;
    transition: transform 0.2s;
}
.mood-btn:hover { transform: scale(1.3); }

.history-section { text-align: left; margin-bottom: 1.5rem; }
.section-header { display: flex; justify-content: space-between; align-items: center; font-size: 0.75rem; font-weight: 700; text-transform: uppercase; opacity: 0.7; margin-bottom: 10px; }
.btn-clear { background: rgba(255,255,255,0.2); border: none; color: white; padding: 4px 8px; border-radius: 4px; font-size: 0.65rem; cursor: pointer; }
.btn-clear:hover { background: rgba(255,255,255,0.3); }

.mood-history {
    display: flex;
    gap: 8px;
    overflow-x: auto;
    padding-bottom: 8px;
}
.mood-history::-webkit-scrollbar { height: 4px; }
.mood-history::-webkit-scrollbar-thumb { background: rgba(255,255,255,0.3); border-radius: 10px; }

.history-item {
    background: rgba(255,255,255,0.1);
    padding: 8px;
    border-radius: 12px;
    min-width: 45px;
    text-align: center;
    display: flex;
    flex-direction: column;
    align-items: center;
    cursor: pointer;
    transition: background 0.2s;
}
.history-item:hover { background: rgba(255,100,100,0.3); }
.history-emoji { font-size: 1.25rem; }
.history-date { font-size: 0.6rem; font-weight: 700; margin-top: 4px; opacity: 0.8; }

.mood-stats {
    display: grid;
    grid-template-columns: 1fr 1fr;
    gap: 12px;
    background: rgba(0,0,0,0.1);
    padding: 12px;
    border-radius: 16px;
}

.stat span { display: block; font-size: 1.25rem; font-weight: 800; }
.stat label { font-size: 0.65rem; font-weight: 700; text-transform: uppercase; opacity: 0.7; }

js:
var history = [];

function render() {
    var list = api.root.getElementById('mood-history');
    list.innerHTML = '';

    var moodCounts = {};
    history.forEach(function(item, idx) {
        var el = document.createElement('div');
        el.className = 'history-item';
        el.innerHTML = '<span class="history-emoji">' + item.emoji + '</span><span class="history-date">' + item.date + '</span>';
        el.title = 'Click to delete';
        el.onclick = function() { deleteMood(idx); };
        list.appendChild(el);

        moodCounts[item.emoji] = (moodCounts[item.emoji] || 0) + 1;
    });

    api.root.getElementById('mood-count').innerText = history.length;

    var topMood = '-';
    var max = 0;
    for (var m in moodCounts) {
        if (moodCounts[m] > max) {
            max = moodCounts[m];
            topMood = m;
        }
    }
    api.root.getElementById('top-mood').innerText = topMood;
}

function logMood(emoji, label) {
    var date = new Date().toLocaleDateString('en-US', { month: 'short', day: 'numeric' });
    history.unshift({ emoji: emoji, label: label, date: date });
    if (history.length > 10) history.pop();
    render();
    api.saveState(history);
}

function deleteMood(idx) {
    history.splice(idx, 1);
    render();
    api.saveState(history);
}

function clearHistory() {
    history = [];
    render();
    api.saveState(history);
}

async function init() {
    var saved = await api.getState();
    if (saved) history = saved;

    api.root.getElementById('mood-amazing').onclick = function() { logMood('ü§©', 'Amazing'); };
    api.root.getElementById('mood-good').onclick = function() { logMood('üòä', 'Good'); };
    api.root.getElementById('mood-neutral').onclick = function() { logMood('üòê', 'Neutral'); };
    api.root.getElementById('mood-low').onclick = function() { logMood('üòî', 'Low'); };
    api.root.getElementById('mood-stressed').onclick = function() { logMood('üò´', 'Stressed'); };
    api.root.getElementById('btn-clear').onclick = clearHistory;

    render();
}

init();
