const { root } = api;

gen = () => {
    const len = root.getElementById('len').value;
    root.getElementById('len-val').innerText = len;

    const charset = "abcdefghijklmnopqrstuvwxyzABCDEFGHIJKLMNOPQRSTUVWXYZ0123456789!@#$%^&*()_+";
    let retVal = "";
    for (let i = 0; i < len; ++i) {
        retVal += charset.charAt(Math.floor(Math.random() * charset.length));
    }

    root.getElementById('pass-out').innerText = retVal;

    const entropy = Math.floor(len * Math.log2(charset.length));
    root.getElementById('entropy-val').innerText = entropy;

    const bar = root.getElementById('strength-bar');
    const percent = Math.min(entropy / 128 * 100, 100);
    bar.style.width = percent + "%";

    let color = "#e06c75";
    if (entropy >= 100) color = "#98c379";
    else if (entropy >= 60) color = "#d19a66";

    bar.style.background = color;
};

gen();
