css:
:host { display: block; margin: 1.5rem 0; font-family: var(--font-interface); }
.widget-card {
    background: #1e293b;
    color: #f8fafc;
    border-radius: 16px;
    padding: 1.5rem;
    box-shadow: 0 10px 25px rgba(0,0,0,0.2);
    border: 1px solid #334155;
}
.widget-title { margin: 0 0 1.5rem 0; font-size: 0.9rem; font-weight: 700; color: #38bdf8; text-transform: uppercase; letter-spacing: 1px; text-align: center; }

.result-container {
    background: #0f172a;
    border-radius: 12px;
    padding: 12px 16px;
    display: flex;
    justify-content: space-between;
    align-items: center;
    margin-bottom: 1.5rem;
    border: 1px solid #334155;
}

.password-display {
    font-family: var(--font-monospace);
    font-size: 1.1rem;
    font-weight: 700;
    color: #38bdf8;
    word-break: break-all;
    margin-right: 12px;
}

.btn-copy {
    background: #334155;
    color: #f8fafc;
    border: none;
    padding: 6px 12px;
    border-radius: 6px;
    font-size: 0.75rem;
    font-weight: 700;
    cursor: pointer;
}
.btn-copy:hover { background: #475569; }

.settings-grid { margin-bottom: 1.5rem; }
.setting-item { margin-bottom: 1rem; }
.setting-item label { display: block; font-size: 0.8rem; font-weight: 600; color: #94a3b8; margin-bottom: 8px; }

input[type="range"] {
    width: 100%;
    accent-color: #38bdf8;
}

.toggles {
    display: grid;
    grid-template-columns: repeat(3, 1fr);
    gap: 8px;
}

.toggle-item {
    display: flex;
    align-items: center;
    gap: 6px;
    font-size: 0.7rem;
    font-weight: 600;
    color: #94a3b8;
    cursor: pointer;
}

.btn-generate {
    width: 100%;
    background: #38bdf8;
    color: #0f172a;
    border: none;
    padding: 12px;
    border-radius: 10px;
    font-weight: 800;
    font-size: 0.9rem;
    cursor: pointer;
    transition: all 0.2s;
}
.btn-generate:hover { background: #7dd3fc; transform: translateY(-2px); }

js:
const { root, saveState, getState } = api;

function updateLen() {
    root.getElementById('len-val').innerText = root.getElementById('len-slider').value;
    generate();
}

function generate() {
    const len = parseInt(root.getElementById('len-slider').value);
    const num = root.getElementById('inc-numbers').checked;
    const sym = root.getElementById('inc-symbols').checked;
    const upper = root.getElementById('inc-upper').checked;

    const lowerChars = "abcdefghijklmnopqrstuvwxyz";
    const upperChars = "ABCDEFGHIJKLMNOPQRSTUVWXYZ";
    const numChars = "0123456789";
    const symChars = "!@#$%^&*()_+~`|}{[]:;?><,./-=";

    let charset = lowerChars;
    if (upper) charset += upperChars;
    if (num) charset += numChars;
    if (sym) charset += symChars;

    let retVal = "";
    for (let i = 0; i < len; ++i) {
        retVal += charset.charAt(Math.floor(Math.random() * charset.length));
    }

    root.getElementById('password-res').innerText = retVal;

    saveState({ len, num, sym, upper });
}

function copyPassword() {
    const pass = root.getElementById('password-res').innerText;
    if (navigator.clipboard && navigator.clipboard.writeText) {
        navigator.clipboard.writeText(pass).then(() => {
            const btn = root.querySelector('.btn-copy');
            const oldText = btn.innerText;
            btn.innerText = "Copied!";
            setTimeout(() => btn.innerText = oldText, 2000);
        });
    }
}

async function init() {
    const saved = await getState();
    if (saved) {
        root.getElementById('len-slider').value = saved.len || 16;
        root.getElementById('inc-numbers').checked = saved.num !== false;
        root.getElementById('inc-symbols').checked = saved.sym !== false;
        root.getElementById('inc-upper').checked = saved.upper !== false;
        updateLen();
    }
    generate();
}

init();
