css:
:host { display: block; margin: 1.5rem 0; font-family: var(--font-interface); }
.widget-card {
    background: #111827;
    color: #f9fafb;
    border-radius: 20px;
    padding: 1.5rem;
    box-shadow: 0 10px 25px rgba(0,0,0,0.3);
    border: 1px solid #374151;
    text-align: center;
}
.widget-title { margin: 0 0 1.5rem 0; font-size: 0.9rem; font-weight: 800; color: #6366f1; text-transform: uppercase; letter-spacing: 2px; }

.input-group {
    display: flex;
    gap: 8px;
    margin-bottom: 1rem;
}
.input-group input {
    flex: 1;
    background: #1f2937;
    border: 1px solid #374151;
    color: white;
    border-radius: 8px;
    padding: 10px 12px;
    font-size: 0.9rem;
    outline: none;
}
.input-group button { background: none; border: none; cursor: pointer; font-size: 1.2rem; }

.strength-bar {
    height: 6px;
    background: #374151;
    border-radius: 10px;
    overflow: hidden;
    margin-bottom: 8px;
}
.strength-fill { height: 100%; width: 0%; transition: all 0.3s; background: #ef4444; }

.strength-label { font-size: 0.8rem; font-weight: 800; margin-bottom: 1.5rem; color: #9ca3af; }

.checklist {
    text-align: left;
    list-style: none;
    padding: 0;
    margin: 0;
    font-size: 0.75rem;
    display: grid;
    gap: 4px;
}
.checklist li { color: #6b7280; display: flex; align-items: center; gap: 8px; }
.checklist li::before { content: "â—‹"; }
.checklist li.valid { color: #10b981; }
.checklist li.valid::before { content: "â—"; }

js:
function checkStrength() {
    var pass = api.root.getElementById('pass-in').value;
    var fill = api.root.getElementById('strength-fill');
    var label = api.root.getElementById('strength-label');

    var score = 0;
    var checks = {
        len: pass.length >= 8,
        upper: /[A-Z]/.test(pass) && /[a-z]/.test(pass),
        num: /[0-9]/.test(pass),
        spec: /[^A-Za-z0-9]/.test(pass)
    };

    for (var k in checks) {
        var el = api.root.getElementById('check-' + k);
        if (checks[k]) {
            el.classList.add('valid');
            score++;
        } else {
            el.classList.remove('valid');
        }
    }

    var colors = ['#ef4444', '#f59e0b', '#eab308', '#84cc16', '#10b981'];
    var labels = ['Too Short', 'Weak', 'Fair', 'Good', 'Strong'];

    var finalScore = pass.length === 0 ? 0 : (pass.length < 8 ? 1 : score + 1);
    fill.style.width = (finalScore / 5) * 100 + '%';
    fill.style.background = colors[finalScore - 1] || '#374151';
    label.innerText = labels[finalScore - 1] || 'Too Short';
    label.style.color = colors[finalScore - 1] || '#9ca3af';
}

function toggleVisibility() {
    var input = api.root.getElementById('pass-in');
    input.type = input.type === 'password' ? 'text' : 'password';
}

async function init() {
    api.root.getElementById('pass-in').oninput = checkStrength;
    api.root.getElementById('btn-toggle').onclick = toggleVisibility;
}

init();
