css:
:host { display: block; margin: 1.5rem 0; font-family: var(--font-interface); }
.widget-card {
    background: #ba4949;
    color: white;
    border-radius: 24px;
    padding: 2rem;
    text-align: center;
    transition: background 0.5s ease;
    box-shadow: 0 10px 30px rgba(0,0,0,0.15);
}

.timer-modes {
    display: flex;
    justify-content: center;
    gap: 8px;
    margin-bottom: 2rem;
}

.mode-btn {
    background: rgba(0,0,0,0.1);
    border: none;
    color: white;
    padding: 6px 12px;
    border-radius: 8px;
    font-weight: 700;
    font-size: 0.85rem;
    cursor: pointer;
    transition: all 0.2s;
}
.mode-btn.active { background: rgba(0,0,0,0.2); }
.mode-btn:hover { background: rgba(0,0,0,0.15); }

.timer-display {
    font-size: 6rem;
    font-weight: 800;
    font-family: var(--font-monospace);
    margin-bottom: 2rem;
    line-height: 1;
}

.controls {
    display: flex;
    align-items: center;
    justify-content: center;
    gap: 16px;
    margin-bottom: 1.5rem;
}

.btn-main {
    background: white;
    color: #ba4949;
    border: none;
    padding: 16px 48px;
    border-radius: 12px;
    font-size: 1.5rem;
    font-weight: 800;
    cursor: pointer;
    box-shadow: 0 6px 0 #ebebeb;
    transition: all 0.1s;
}
.btn-main:active { transform: translateY(4px); box-shadow: 0 2px 0 #ebebeb; }

.btn-reset {
    background: none;
    border: none;
    color: white;
    font-size: 2rem;
    cursor: pointer;
    opacity: 0.8;
}
.btn-reset:hover { opacity: 1; }

.stats { font-size: 0.9rem; font-weight: 600; opacity: 0.8; }

js:
const { root, saveState, getState } = api;

let timeLeft = 1500;
let timerId = null;
let sessions = 0;
let currentMode = 1500;

const display = root.getElementById('timer-display');
const startBtn = root.getElementById('start-btn');
const sessionEl = root.getElementById('session-count');
const card = root.querySelector('.widget-card');

function updateDisplay() {
    const mins = Math.floor(timeLeft / 60);
    const secs = timeLeft % 60;
    display.innerText = `${mins}:${secs < 10 ? '0' : ''}${secs}`;
}

function setMode(btn) {
    const time = parseInt(btn.dataset.time);
    currentMode = time;
    timeLeft = time;

    root.querySelectorAll('.mode-btn').forEach(b => b.classList.remove('active'));
    btn.classList.add('active');

    if (time === 1500) card.style.background = '#ba4949';
    else if (time === 300) card.style.background = '#38858a';
    else card.style.background = '#397097';

    resetTimer();
}

function toggleTimer() {
    if (timerId) {
        clearInterval(timerId);
        timerId = null;
        startBtn.innerText = "START";
    } else {
        startBtn.innerText = "STOP";
        timerId = setInterval(() => {
            timeLeft--;
            updateDisplay();
            if (timeLeft <= 0) {
                clearInterval(timerId);
                timerId = null;
                startBtn.innerText = "START";
                if (currentMode === 1500) {
                    sessions++;
                    sessionEl.innerText = sessions;
                    saveState({ sessions });
                }
            }
        }, 1000);
    }
}

function resetTimer() {
    clearInterval(timerId);
    timerId = null;
    timeLeft = currentMode;
    startBtn.innerText = "START";
    updateDisplay();
}

async function init() {
    const saved = await getState();
    if (saved) {
        sessions = saved.sessions || 0;
        sessionEl.innerText = sessions;
    }
    updateDisplay();
}

init();
