css:
:host { display: block; margin: 1.5rem 0; font-family: var(--font-interface); }
.widget-card {
    background: #ba4949;
    color: white;
    border-radius: 24px;
    padding: 2rem;
    text-align: center;
    transition: background 0.5s ease;
    box-shadow: 0 10px 30px rgba(0,0,0,0.15);
}

.timer-modes {
    display: flex;
    justify-content: center;
    gap: 8px;
    margin-bottom: 2rem;
}

.mode-btn {
    background: rgba(0,0,0,0.1);
    border: none;
    color: white;
    padding: 6px 12px;
    border-radius: 8px;
    font-weight: 700;
    font-size: 0.85rem;
    cursor: pointer;
    transition: all 0.2s;
}
.mode-btn.active { background: rgba(0,0,0,0.2); }
.mode-btn:hover { background: rgba(0,0,0,0.15); }

.timer-display {
    font-size: 6rem;
    font-weight: 800;
    font-family: var(--font-monospace);
    margin-bottom: 2rem;
    line-height: 1;
}

.controls {
    display: flex;
    align-items: center;
    justify-content: center;
    gap: 16px;
    margin-bottom: 1.5rem;
}

.btn-main {
    background: white;
    color: #ba4949;
    border: none;
    padding: 16px 48px;
    border-radius: 12px;
    font-size: 1.5rem;
    font-weight: 800;
    cursor: pointer;
    box-shadow: 0 6px 0 #ebebeb;
    transition: all 0.1s;
}
.btn-main:active { transform: translateY(4px); box-shadow: 0 2px 0 #ebebeb; }

.btn-reset {
    background: none;
    border: none;
    color: white;
    font-size: 2rem;
    cursor: pointer;
    opacity: 0.8;
}
.btn-reset:hover { opacity: 1; }

.stats { font-size: 0.9rem; font-weight: 600; opacity: 0.8; }
.btn-clear { background: none; border: none; color: white; text-decoration: underline; cursor: pointer; opacity: 0.6; margin-left: 8px; }
.btn-clear:hover { opacity: 1; }

js:
var timeLeft = 1500;
var timerId = null;
var sessions = 0;
var currentMode = 1500;

function updateDisplay() {
    var mins = Math.floor(timeLeft / 60);
    var secs = timeLeft % 60;
    api.root.getElementById('timer-display').innerText = mins + ':' + (secs < 10 ? '0' : '') + secs;
}

function setMode(time, btn) {
    currentMode = time;
    timeLeft = time;

    var card = api.root.querySelector('.widget-card');
    api.root.querySelectorAll('.mode-btn').forEach(function(b) { b.classList.remove('active'); });
    btn.classList.add('active');

    if (time === 1500) card.style.background = '#ba4949';
    else if (time === 300) card.style.background = '#38858a';
    else card.style.background = '#397097';

    resetTimer();
}

function toggleTimer() {
    var startBtn = api.root.getElementById('start-btn');
    if (timerId) {
        clearInterval(timerId);
        timerId = null;
        startBtn.innerText = 'START';
    } else {
        startBtn.innerText = 'STOP';
        timerId = setInterval(function() {
            timeLeft--;
            updateDisplay();
            if (timeLeft <= 0) {
                clearInterval(timerId);
                timerId = null;
                startBtn.innerText = 'START';
                if (currentMode === 1500) {
                    sessions++;
                    api.root.getElementById('session-count').innerText = sessions;
                    api.saveState({ sessions: sessions });
                }
            }
        }, 1000);
    }
}

function resetTimer() {
    clearInterval(timerId);
    timerId = null;
    timeLeft = currentMode;
    api.root.getElementById('start-btn').innerText = 'START';
    updateDisplay();
}

function clearSessions() {
    sessions = 0;
    api.root.getElementById('session-count').innerText = 0;
    api.saveState({ sessions: 0 });
}

async function init() {
    var saved = await api.getState();
    if (saved) {
        sessions = saved.sessions || 0;
        api.root.getElementById('session-count').innerText = sessions;
    }

    api.root.getElementById('mode-pomo').onclick = function() { setMode(1500, this); };
    api.root.getElementById('mode-short').onclick = function() { setMode(300, this); };
    api.root.getElementById('mode-long').onclick = function() { setMode(900, this); };
    api.root.getElementById('start-btn').onclick = toggleTimer;
    api.root.getElementById('reset-btn').onclick = resetTimer;
    api.root.getElementById('btn-clear').onclick = clearSessions;

    updateDisplay();
}

init();
