css:
:host { display: block; margin: 1.5rem 0; font-family: var(--font-interface); }
.widget-card {
    background: #ffffff;
    color: #1e293b;
    border-radius: 20px;
    padding: 1.5rem;
    box-shadow: 0 10px 25px rgba(0,0,0,0.05);
    border: 1px solid #e2e8f0;
}
.widget-header { display: flex; justify-content: space-between; align-items: center; margin-bottom: 2rem; }
.widget-title { margin: 0; font-size: 0.9rem; font-weight: 800; color: #64748b; text-transform: uppercase; letter-spacing: 1px; }

.btn-add {
    background: #1e293b;
    color: white;
    border: none;
    width: 28px;
    height: 28px;
    border-radius: 50%;
    font-weight: 800;
    cursor: pointer;
}

.timeline-container {
    position: relative;
    padding-left: 30px;
    margin-bottom: 2rem;
    max-height: 300px;
    overflow-y: auto;
}

.timeline-line {
    position: absolute;
    left: 7px;
    top: 0;
    bottom: 0;
    width: 2px;
    background: #e2e8f0;
}

.milestone {
    position: relative;
    margin-bottom: 24px;
}

.milestone-dot {
    position: absolute;
    left: -30px;
    top: 4px;
    width: 16px;
    height: 16px;
    border-radius: 50%;
    background: white;
    border: 3px solid #cbd5e1;
    z-index: 1;
    cursor: pointer;
    transition: all 0.2s;
}
.milestone.done .milestone-dot {
    background: #10b981;
    border-color: #10b981;
    box-shadow: 0 0 8px rgba(16, 185, 129, 0.4);
}

.milestone-content {
    display: flex;
    flex-direction: column;
    gap: 2px;
}

.milestone-title {
    font-weight: 700;
    font-size: 0.95rem;
    color: #1e293b;
}
.milestone.done .milestone-title { text-decoration: line-through; color: #94a3b8; }

.milestone-date {
    font-size: 0.75rem;
    font-weight: 600;
    color: #64748b;
}

.timeline-stats {
    display: grid;
    grid-template-columns: 1fr 1fr;
    gap: 12px;
    border-top: 1px solid #f1f5f9;
    padding-top: 1.25rem;
}

.stat-box { text-align: center; }
.stat-box span { display: block; font-size: 1.25rem; font-weight: 800; color: #1e293b; }
.stat-box label { font-size: 0.65rem; font-weight: 700; text-transform: uppercase; color: #64748b; }

js:
const { root, saveState, getState } = api;

let milestones = [
    { title: "Project Kickoff", date: "Jan 10", done: true },
    { title: "Design Phase", date: "Jan 15", done: false },
    { title: "Development", date: "Feb 01", done: false }
];

function render() {
    const container = root.getElementById('timeline');
    container.innerHTML = '<div class="timeline-line"></div>';

    let doneCount = 0;

    milestones.forEach((m, idx) => {
        if (m.done) doneCount++;
        const item = document.createElement('div');
        item.className = `milestone ${m.done ? 'done' : ''}`;
        item.innerHTML = `
            <div class="milestone-dot" onclick="toggleMilestone(${idx})"></div>
            <div class="milestone-content">
                <span class="milestone-title">${m.title}</span>
                <span class="milestone-date">${m.date}</span>
            </div>
        `;
        container.appendChild(item);
    });

    root.getElementById('completed-count').innerText = doneCount;
    root.getElementById('pending-count').innerText = milestones.length - doneCount;
}

function addMilestone() {
    milestones.push({ title: "New Milestone", date: "TBD", done: false });
    render();
    saveState(milestones);
}

function toggleMilestone(idx) {
    milestones[idx].done = !milestones[idx].done;
    render();
    saveState(milestones);
}

async function init() {
    const saved = await getState();
    if (saved) milestones = saved;
    render();
}

init();
