css:
:host { display: block; margin: 1.5rem 0; font-family: var(--font-interface); }
.widget-card {
    background: #fdfcf0;
    color: #44403c;
    border-radius: 20px;
    padding: 1.5rem;
    box-shadow: 0 10px 25px rgba(0,0,0,0.05);
    border: 1px solid #e7e5e4;
}
.widget-header { display: flex; justify-content: space-between; align-items: center; margin-bottom: 1.5rem; }
.widget-title { margin: 0; font-size: 0.9rem; font-weight: 800; color: #78716c; text-transform: uppercase; letter-spacing: 1px; }

.btn-add {
    background: #44403c;
    color: white;
    border: none;
    width: 28px;
    height: 28px;
    border-radius: 50%;
    font-weight: 800;
    cursor: pointer;
}

.book-grid {
    display: grid;
    gap: 12px;
    margin-bottom: 1.5rem;
    max-height: 300px;
    overflow-y: auto;
}

.book-item {
    background: white;
    border-radius: 12px;
    padding: 12px;
    border: 1px solid #e7e5e4;
    display: flex;
    gap: 12px;
    align-items: center;
}

.book-cover {
    width: 40px;
    height: 60px;
    background: #d6d3d1;
    border-radius: 4px;
    display: flex;
    align-items: center;
    justify-content: center;
    font-size: 1.5rem;
}

.book-info {
    flex: 1;
    display: flex;
    flex-direction: column;
    gap: 4px;
}

.book-title { font-weight: 700; font-size: 0.9rem; }
.book-author { font-size: 0.75rem; color: #78716c; }

.progress-bar {
    height: 6px;
    background: #f5f5f4;
    border-radius: 10px;
    overflow: hidden;
    margin-top: 4px;
}
.progress-fill { height: 100%; background: #a8a29e; transition: width 0.3s; }

.book-actions { display: flex; gap: 8px; }
.btn-progress { background: none; border: none; cursor: pointer; font-size: 0.7rem; color: #78716c; }
.btn-del { background: none; border: none; cursor: pointer; color: #ef4444; opacity: 0.5; }
.btn-del:hover { opacity: 1; }

.stats-row {
    display: flex;
    justify-content: space-around;
    border-top: 1px solid #e7e5e4;
    padding-top: 1rem;
}

.stat-item { text-align: center; }
.stat-item span { display: block; font-size: 1.2rem; font-weight: 800; }
.stat-item label { font-size: 0.65rem; font-weight: 700; text-transform: uppercase; color: #78716c; }

js:
var books = [
    { title: 'Atomic Habits', author: 'James Clear', pages: 320, current: 150, emoji: 'ðŸ”‹' },
    { title: 'Deep Work', author: 'Cal Newport', pages: 296, current: 296, emoji: 'ðŸ§ ' }
];

function render() {
    var grid = api.root.getElementById('book-grid');
    grid.innerHTML = '';

    var readCount = 0;
    var totalPages = 0;

    books.forEach(function(book, idx) {
        var isRead = book.current >= book.pages;
        if (isRead) readCount++;
        totalPages += book.current;

        var item = document.createElement('div');
        item.className = 'book-item';
        var percent = Math.round((book.current / book.pages) * 100);

        item.innerHTML = '<div class="book-cover">' + (book.emoji || 'ðŸ“–') + '</div>' +
            '<div class="book-info">' +
                '<span class="book-title">' + book.title + '</span>' +
                '<span class="book-author">' + book.author + '</span>' +
                '<div class="progress-bar"><div class="progress-fill" style="width: ' + percent + '%"></div></div>' +
            '</div>' +
            '<div class="book-actions">' +
                '<button class="btn-progress">' + percent + '%</button>' +
                '<button class="btn-del">Ã—</button>' +
            '</div>';

        item.querySelector('.btn-progress').onclick = function() { updateProgress(idx); };
        item.querySelector('.btn-del').onclick = function() { deleteBook(idx); };
        grid.appendChild(item);
    });

    api.root.getElementById('books-read').innerText = readCount;
    api.root.getElementById('pages-total').innerText = totalPages;
}

function addBook() {
    var title = prompt('Book title?', 'New Book');
    if (title) {
        books.push({ title: title, author: 'Unknown', pages: 200, current: 0, emoji: 'ðŸ“–' });
        render();
        api.saveState(books);
    }
}

function updateProgress(idx) {
    books[idx].current = Math.min(books[idx].current + 20, books[idx].pages);
    render();
    api.saveState(books);
}

function deleteBook(idx) {
    books.splice(idx, 1);
    render();
    api.saveState(books);
}

async function init() {
    var saved = await api.getState();
    if (saved) books = saved;

    api.root.getElementById('btn-add').onclick = addBook;

    render();
}

init();
