:host { display: block; font-family: var(--font-interface); }
* { box-sizing: border-box; }

.widget-card {
    background: var(--background-secondary);
    border: 1px solid var(--background-modifier-border);
    border-radius: 24px;
    padding: 1.25rem;
    box-shadow: var(--shadow-s);
    position: relative;
    overflow: hidden;
    min-height: 450px;
}

.widget-header {
    display: flex;
    justify-content: space-between;
    align-items: center;
    margin-bottom: 1rem;
}

.header-left {
    display: flex;
    align-items: center;
    gap: 8px;
}

.widget-icon { font-size: 1.2rem; }
.widget-title {
    margin: 0;
    font-size: 0.85rem;
    font-weight: 800;
    color: var(--text-normal);
    text-transform: uppercase;
    letter-spacing: 1px;
}

.btn-icon {
    background: var(--background-primary);
    border: 1px solid var(--background-modifier-border);
    color: var(--text-normal);
    width: 32px;
    height: 32px;
    border-radius: 10px;
    cursor: pointer;
    display: flex;
    align-items: center;
    justify-content: center;
    transition: all 0.2s;
}
.btn-icon:hover { background: var(--background-modifier-hover); transform: scale(1.05); }

/* Presets */
.presets-container {
    margin-bottom: 1.25rem;
}
.presets-header {
    display: flex;
    justify-content: space-between;
    align-items: center;
    margin-bottom: 8px;
}
.presets-label {
    font-size: 0.65rem;
    font-weight: 700;
    color: var(--text-faint);
    text-transform: uppercase;
}
.btn-add-preset {
    background: var(--interactive-accent);
    color: white;
    border: none;
    width: 20px;
    height: 20px;
    border-radius: 6px;
    font-size: 0.8rem;
    font-weight: 800;
    cursor: pointer;
    display: flex;
    align-items: center;
    justify-content: center;
    transition: all 0.2s;
}
.btn-add-preset:hover { transform: scale(1.1); }

.presets-list {
    display: flex;
    flex-wrap: wrap;
    gap: 8px;
    margin-top: 4px;
}

.preset-chip {
    background: var(--background-primary);
    border: 1px solid var(--background-modifier-border);
    color: var(--text-muted);
    padding: 6px 10px;
    border-radius: 20px;
    font-size: 0.75rem;
    font-weight: 600;
    white-space: nowrap;
    cursor: pointer;
    transition: all 0.2s;
    display: flex;
    align-items: center;
    gap: 6px;
}
.preset-chip:hover {
    background: var(--background-modifier-hover);
    color: var(--text-normal);
    border-color: var(--interactive-accent);
}

.btn-del-preset {
    font-size: 0.6rem;
    opacity: 0.4;
    transition: opacity 0.2s;
    padding: 2px;
}
.preset-chip:hover .btn-del-preset {
    opacity: 1;
}
.btn-del-preset:hover {
    color: var(--text-error);
}

/* Inputs */
.input-section {
    display: flex;
    flex-direction: column;
    gap: 1rem;
    margin-bottom: 1.25rem;
}

.label-row {
    display: flex;
    justify-content: space-between;
    align-items: center;
    margin-bottom: 6px;
}

label {
    font-size: 0.65rem;
    font-weight: 700;
    color: var(--text-faint);
    text-transform: uppercase;
}

.flags-group {
    display: flex;
    gap: 4px;
}

.flag-btn {
    background: var(--background-primary);
    border: 1px solid var(--background-modifier-border);
    color: var(--text-faint);
    width: 24px;
    height: 24px;
    border-radius: 6px;
    font-size: 0.7rem;
    font-weight: 700;
    cursor: pointer;
    transition: all 0.2s;
}
.flag-btn.active {
    background: var(--interactive-accent);
    color: white;
    border-color: var(--interactive-accent);
}

.pattern-input-wrapper {
    display: flex;
    align-items: center;
    background: var(--background-primary);
    border: 1px solid var(--background-modifier-border);
    border-radius: 12px;
    padding: 0 12px;
    gap: 4px;
}
.pattern-input-wrapper:focus-within {
    border-color: var(--interactive-accent);
    box-shadow: 0 0 0 2px rgba(var(--interactive-accent-rgb), 0.1);
}

.slash {
    color: var(--text-faint);
    font-family: var(--font-monospace);
    font-weight: 700;
}

#regex-pattern {
    flex: 1;
    background: transparent;
    border: none;
    color: var(--text-accent);
    padding: 10px 0;
    font-family: var(--font-monospace);
    font-size: 0.9rem;
    outline: none;
}

.active-flags-text {
    color: var(--text-accent);
    font-family: var(--font-monospace);
    font-weight: 700;
    font-size: 0.9rem;
}

#regex-input {
    width: 100%;
    height: 100px;
    background: var(--background-primary);
    border: 1px solid var(--background-modifier-border);
    color: var(--text-normal);
    padding: 12px;
    border-radius: 12px;
    font-family: var(--font-monospace);
    font-size: 0.85rem;
    outline: none;
    resize: vertical;
}
#regex-input:focus { border-color: var(--interactive-accent); }

/* Output */
.output-header {
    display: flex;
    justify-content: space-between;
    align-items: center;
    margin-bottom: 8px;
}

.output-label-group {
    display: flex;
    align-items: center;
    gap: 10px;
}

.output-controls {
    display: flex;
    gap: 8px;
    align-items: center;
}

.separator-select {
    display: flex;
    background: var(--background-primary);
    padding: 2px;
    border-radius: 8px;
    border: 1px solid var(--background-modifier-border);
}

.sep-btn {
    background: transparent;
    border: none;
    color: var(--text-muted);
    width: 24px;
    height: 24px;
    border-radius: 6px;
    font-size: 0.7rem;
    font-weight: 700;
    cursor: pointer;
    transition: all 0.2s;
    display: flex;
    align-items: center;
    justify-content: center;
}
.sep-btn.active {
    background: var(--background-modifier-hover);
    color: var(--interactive-accent);
}

.btn-copy-small {
    background: var(--background-modifier-border);
    border: none;
    color: var(--text-muted);
    font-size: 0.6rem;
    padding: 2px 8px;
    border-radius: 4px;
    cursor: pointer;
    font-weight: 700;
    text-transform: uppercase;
    transition: all 0.2s;
}
.btn-copy-small:hover { background: var(--background-modifier-hover); color: var(--text-normal); }

.mode-toggle {
    display: flex;
    background: var(--background-primary);
    padding: 2px;
    border-radius: 8px;
    border: 1px solid var(--background-modifier-border);
}

.mode-btn {
    background: transparent;
    border: none;
    color: var(--text-muted);
    padding: 4px 10px;
    border-radius: 6px;
    font-size: 0.65rem;
    font-weight: 700;
    cursor: pointer;
    transition: all 0.2s;
}
.mode-btn.active {
    background: var(--background-modifier-hover);
    color: var(--text-normal);
    box-shadow: var(--shadow-s);
}

.regex-match-box {
    background: var(--background-primary);
    border: 1px solid var(--background-modifier-border);
    padding: 12px;
    border-radius: 12px;
    font-family: var(--font-monospace);
    font-size: 0.85rem;
    line-height: 1.6;
    min-height: 60px;
    max-height: 200px;
    overflow-y: auto;
    word-break: break-all;
    white-space: pre-wrap;
}

.highlight {
    background: rgba(var(--interactive-accent-rgb), 0.2);
    border-bottom: 2px solid var(--interactive-accent);
    border-radius: 2px;
    color: var(--text-normal);
}

.match-stats {
    font-size: 0.65rem;
    color: var(--text-faint);
    margin-top: 6px;
    text-align: right;
    font-weight: 600;
}

.extract-item {
    display: inline-block;
    background: var(--background-secondary);
    border: 1px solid var(--background-modifier-border);
    padding: 2px 8px;
    border-radius: 4px;
    color: var(--text-accent);
}

.sep-visual {
    color: var(--text-faint);
    font-weight: 800;
    margin: 0 4px;
}

/* Overlays */
.overlay {
    position: absolute;
    top: 0; left: 0; width: 100%; height: 100%;
    background: rgba(0,0,0,0.7);
    backdrop-filter: blur(88px);
    display: none;
    align-items: center;
    justify-content: center;
    z-index: 100;
    padding: 0.5rem;
}
.overlay.show { display: flex; }

.overlay-content {
    background: var(--background-primary);
    padding: 1.25rem;
    border-radius: 20px;
    width: 100%;
    max-width: 280px;
    box-shadow: 0 15px 40px rgba(0,0,0,0.4);
}

.overlay-content h3 { margin: 0 0 1rem 0; font-size: 0.9rem; color: var(--text-accent); }

.form-body { display: flex; flex-direction: column; gap: 12px; }

.input-field input {
    width: 100%;
    background: var(--background-secondary);
    border: 1px solid var(--background-modifier-border);
    color: var(--text-normal);
    padding: 8px 12px;
    border-radius: 8px;
    outline: none;
    font-size: 0.8rem;
}

.overlay-actions {
    display: flex;
    gap: 8px;
    margin-top: 1.25rem;
}

.btn-save, .btn-cancel {
    flex: 1;
    padding: 8px;
    border-radius: 8px;
    font-weight: 700;
    cursor: pointer;
    border: none;
    font-size: 0.75rem;
}
.btn-save { background: var(--interactive-accent); color: white; }
.btn-cancel { background: var(--background-modifier-border); color: var(--text-normal); }

/* Custom Scrollbar */
.regex-match-box::-webkit-scrollbar {
    width: 4px;
}
.regex-match-box::-webkit-scrollbar-thumb {
    background: var(--background-modifier-border);
    border-radius: 10px;
}

