css:
:host { display: block; margin: 1.5rem 0; font-family: var(--font-interface); }
.widget-card {
    background: #1e293b;
    color: white;
    border-radius: 24px;
    padding: 1.5rem;
    box-shadow: 0 10px 25px rgba(0,0,0,0.3);
    border: 1px solid #334155;
    max-width: 280px;
    margin: 1.5rem auto;
}

.calc-display {
    background: #0f172a;
    color: #38bdf8;
    padding: 20px;
    font-size: 2rem;
    font-weight: 800;
    text-align: right;
    border-radius: 16px;
    margin-bottom: 1.5rem;
    font-family: var(--font-monospace);
    overflow: hidden;
    text-overflow: ellipsis;
}

.calc-grid {
    display: grid;
    grid-template-columns: repeat(4, 1fr);
    gap: 10px;
}

.calc-grid button {
    background: #334155;
    border: none;
    color: white;
    padding: 15px 0;
    font-size: 1.2rem;
    font-weight: 700;
    border-radius: 12px;
    cursor: pointer;
    transition: background 0.2s;
}
.calc-grid button:hover { background: #475569; }
.calc-grid button:active { transform: scale(0.95); }

.btn-op { background: #475569 !important; color: #38bdf8 !important; }
.btn-eq { background: #38bdf8 !important; color: #0f172a !important; grid-row: span 2; height: 100%; }
.btn-zero { grid-column: span 2; }

js:
var currentInput = '0';

function updateDisplay() {
    api.root.getElementById('calc-display').innerText = currentInput;
}

function append(val) {
    if (currentInput === '0' && val !== '.') currentInput = val;
    else currentInput += val;
    updateDisplay();
    api.saveState(currentInput);
}

function clearCalc() {
    currentInput = '0';
    updateDisplay();
    api.saveState(currentInput);
}

function backspace() {
    currentInput = currentInput.slice(0, -1) || '0';
    updateDisplay();
    api.saveState(currentInput);
}

function calculate() {
    try {
        var result = new Function('return ' + currentInput)();
        currentInput = String(Number(result.toFixed(8)));
        updateDisplay();
        api.saveState(currentInput);
    } catch(e) {
        currentInput = 'Error';
        updateDisplay();
        setTimeout(clearCalc, 1000);
    }
}

async function init() {
    var saved = await api.getState();
    if (saved) currentInput = saved;

    // Number and dot buttons
    api.root.querySelectorAll('.btn-num').forEach(function(btn) {
        btn.onclick = function() { append(btn.dataset.val); };
    });

    // Operation buttons
    api.root.getElementById('btn-clear').onclick = clearCalc;
    api.root.getElementById('btn-back').onclick = backspace;
    api.root.getElementById('btn-eq').onclick = calculate;
    api.root.getElementById('btn-div').onclick = function() { append('/'); };
    api.root.getElementById('btn-mul').onclick = function() { append('*'); };
    api.root.getElementById('btn-sub').onclick = function() { append('-'); };
    api.root.getElementById('btn-add').onclick = function() { append('+'); };

    updateDisplay();
}

init();
