css:
:host { display: block; margin: 1.5rem 0; font-family: var(--font-interface); }
.widget-card {
    background: #1e293b;
    color: white;
    border-radius: 24px;
    padding: 1.5rem;
    box-shadow: 0 10px 25px rgba(0,0,0,0.3);
    border: 1px solid #334155;
    max-width: 280px;
    margin: 1.5rem auto;
}

.calc-display {
    background: #0f172a;
    color: #38bdf8;
    padding: 20px;
    font-size: 2rem;
    font-weight: 800;
    text-align: right;
    border-radius: 16px;
    margin-bottom: 1.5rem;
    font-family: var(--font-monospace);
    overflow: hidden;
    text-overflow: ellipsis;
}

.calc-grid {
    display: grid;
    grid-template-columns: repeat(4, 1fr);
    gap: 10px;
}

.calc-grid button {
    background: #334155;
    border: none;
    color: white;
    padding: 15px 0;
    font-size: 1.2rem;
    font-weight: 700;
    border-radius: 12px;
    cursor: pointer;
    transition: background 0.2s;
}
.calc-grid button:hover { background: #475569; }
.calc-grid button:active { transform: scale(0.95); }

.btn-op { background: #475569 !important; color: #38bdf8 !important; }
.btn-eq { background: #38bdf8 !important; color: #0f172a !important; grid-row: span 2; height: 100%; }
.btn-zero { grid-column: span 2; }

js:
const { root, saveState, getState } = api;

let currentInput = "0";

async function init() {
    const saved = await getState();
    if (saved) currentInput = saved;
    updateDisplay();
}

function updateDisplay() {
    root.getElementById('calc-display').innerText = currentInput;
}

append = async (val) => {
    if (currentInput === "0" && val !== ".") currentInput = val;
    else currentInput += val;
    updateDisplay();
    await saveState(currentInput);
};

clearCalc = async () => {
    currentInput = "0";
    updateDisplay();
    await saveState(currentInput);
};

backspace = async () => {
    currentInput = currentInput.slice(0, -1) || "0";
    updateDisplay();
    await saveState(currentInput);
};

calculate = async () => {
    try {
        // Use Function instead of eval for a bit more safety
        const result = new Function(`return ${currentInput}`)();
        currentInput = String(Number(result.toFixed(8)));
        updateDisplay();
        await saveState(currentInput);
    } catch(e) {
        currentInput = "Error";
        updateDisplay();
        setTimeout(clearCalc, 1000);
    }
};

init();
