css:
:host { display: block; margin: 1.5rem 0; font-family: var(--font-interface); }
.widget-card {
    background: #1e1b4b;
    color: #e0e7ff;
    border-radius: 24px;
    padding: 1.5rem;
    box-shadow: 0 10px 25px rgba(0,0,0,0.4);
    border: 1px solid #312e81;
}
.widget-title { margin: 0 0 1.5rem 0; font-size: 1rem; font-weight: 800; color: #818cf8; text-transform: uppercase; letter-spacing: 1px; }

.input-section {
    display: grid;
    grid-template-columns: 1fr 1fr;
    gap: 12px;
    margin-bottom: 1.5rem;
}
.input-group { text-align: left; }
.input-group label { display: block; font-size: 0.7rem; font-weight: 700; color: #818cf8; margin-bottom: 4px; text-transform: uppercase; }
.input-group input {
    width: 100%;
    background: #312e81;
    border: 1px solid #4338ca;
    color: white;
    border-radius: 8px;
    padding: 8px;
    font-size: 0.9rem;
    outline: none;
}

.btn-log {
    grid-column: 1 / -1;
    background: #6366f1;
    color: white;
    border: none;
    padding: 10px;
    border-radius: 10px;
    font-weight: 700;
    cursor: pointer;
    margin-top: 8px;
}

.history-list {
    display: flex;
    gap: 8px;
    overflow-x: auto;
    margin-bottom: 1.5rem;
    padding-bottom: 8px;
}
.history-item {
    background: rgba(255,255,255,0.05);
    padding: 8px;
    border-radius: 12px;
    min-width: 60px;
    text-align: center;
    border: 1px solid rgba(255,255,255,0.1);
}
.h-val { display: block; font-weight: 800; font-size: 1rem; }
.h-date { font-size: 0.6rem; opacity: 0.6; }

.stats {
    display: grid;
    grid-template-columns: 1fr 1fr;
    gap: 12px;
    border-top: 1px solid #312e81;
    padding-top: 1.25rem;
}
.stat-item span { display: block; font-size: 1.25rem; font-weight: 800; color: #818cf8; }
.stat-item label { font-size: 0.65rem; font-weight: 700; text-transform: uppercase; opacity: 0.6; }

js:
var history = [];

function render() {
    var list = api.root.getElementById('sleep-history');
    list.innerHTML = '';

    var totalH = 0;
    var totalQ = 0;

    history.forEach(function(item) {
        totalH += parseFloat(item.hours);
        totalQ += parseInt(item.quality);

        var el = document.createElement('div');
        el.className = 'history-item';
        el.innerHTML = '<span class="h-val">' + item.hours + 'h</span><span class="h-date">' + item.date + '</span>';
        list.appendChild(el);
    });

    var avgH = history.length > 0 ? (totalH / history.length).toFixed(1) : 0;
    var avgQ = history.length > 0 ? (totalQ / history.length).toFixed(1) : 0;

    api.root.getElementById('avg-hours').innerText = avgH;
    api.root.getElementById('avg-quality').innerText = avgQ;
}

function logSleep() {
    var hours = api.root.getElementById('sleep-hours').value;
    var quality = api.root.getElementById('sleep-quality').value;

    if (hours && quality) {
        var date = new Date().toLocaleDateString('en-US', { month: 'short', day: 'numeric' });
        history.unshift({ hours: hours, quality: quality, date: date });
        if (history.length > 7) history.pop();

        api.root.getElementById('sleep-hours').value = '';
        api.root.getElementById('sleep-quality').value = '';

        render();
        api.saveState(history);
    }
}

async function init() {
    var saved = await api.getState();
    if (saved) history = saved;

    api.root.getElementById('btn-log').onclick = logSleep;

    render();
}

init();
