css:
:host { display: block; margin: 1.5rem 0; font-family: var(--font-interface); }
.widget-card {
    background: #111827;
    color: #f9fafb;
    border-radius: 24px;
    padding: 1.5rem;
    box-shadow: 0 10px 25px rgba(0,0,0,0.3);
    border: 1px solid #374151;
}
.widget-header { display: flex; justify-content: space-between; align-items: center; margin-bottom: 1.5rem; }
.widget-title { margin: 0; font-size: 0.9rem; font-weight: 800; color: #10b981; text-transform: uppercase; letter-spacing: 1px; }
.goal { font-size: 0.7rem; font-weight: 700; color: #9ca3af; }

.main-display { text-align: center; margin-bottom: 1.5rem; }
.steps { font-size: 4rem; font-weight: 900; line-height: 1; color: #10b981; }
.label { font-size: 0.8rem; font-weight: 700; color: #9ca3af; text-transform: uppercase; margin-top: 4px; }

.progress-bar {
    height: 12px;
    background: #374151;
    border-radius: 100px;
    overflow: hidden;
    margin-bottom: 1.5rem;
}
.progress-fill { height: 100%; background: #10b981; width: 0%; transition: width 0.5s ease; }

.controls {
    display: flex;
    gap: 8px;
    justify-content: center;
    margin-bottom: 1.5rem;
}
.controls button {
    background: #374151;
    color: white;
    border: none;
    padding: 8px 16px;
    border-radius: 10px;
    font-weight: 700;
    cursor: pointer;
}
.controls button:hover { background: #4b5563; }
.btn-reset { background: #ef4444 !important; width: 40px; }

.weekly-chart {
    display: flex;
    align-items: flex-end;
    justify-content: space-between;
    height: 60px;
    gap: 4px;
    padding-top: 10px;
    border-top: 1px solid #374151;
}
.bar {
    flex: 1;
    background: #10b981;
    opacity: 0.3;
    border-radius: 2px 2px 0 0;
    min-height: 2px;
    position: relative;
}
.bar.today { opacity: 1; }

js:
var state = {
    today: 0,
    history: [4500, 8200, 11000, 7600, 9100, 5400, 0]
};

function render() {
    api.root.getElementById('step-count').innerText = state.today.toLocaleString();
    var percent = Math.min((state.today / 10000) * 100, 100);
    api.root.getElementById('step-fill').style.width = percent + '%';

    var chart = api.root.getElementById('step-chart');
    chart.innerHTML = '';
    state.history.forEach(function(val, idx) {
        var bar = document.createElement('div');
        bar.className = 'bar' + (idx === 6 ? ' today' : '');
        var h = Math.min((val / 12000) * 100, 100);
        bar.style.height = h + '%';
        chart.appendChild(bar);
    });
}

function addSteps(amount) {
    state.today += amount;
    state.history[6] = state.today;
    render();
    api.saveState(state);
}

function resetSteps() {
    state.today = 0;
    state.history[6] = 0;
    render();
    api.saveState(state);
}

async function init() {
    var saved = await api.getState();
    if (saved) state = saved;

    api.root.getElementById('btn-add-500').onclick = function() { addSteps(500); };
    api.root.getElementById('btn-add-1000').onclick = function() { addSteps(1000); };
    api.root.getElementById('btn-reset').onclick = resetSteps;

    render();
}

init();
