css:
:host { display: block; margin: 1.5rem 0; font-family: var(--font-interface); }
.widget-card {
    background: #0f172a;
    color: #f8fafc;
    border-radius: 20px;
    padding: 1.5rem;
    box-shadow: 0 10px 25px rgba(0,0,0,0.3);
    border: 1px solid #1e293b;
}
.widget-title { margin: 0 0 1.5rem 0; font-size: 0.9rem; font-weight: 800; color: #38bdf8; text-transform: uppercase; letter-spacing: 1px; }

.stock-list { display: grid; gap: 10px; margin-bottom: 1.5rem; }
.stock-item {
    display: flex;
    justify-content: space-between;
    align-items: center;
    background: #1e293b;
    padding: 12px;
    border-radius: 12px;
    border: 1px solid #334155;
}

.stock-info { display: flex; flex-direction: column; text-align: left; }
.symbol { font-weight: 800; font-size: 1rem; }
.price { font-family: var(--font-monospace); font-weight: 700; }

.stock-right { display: flex; align-items: center; gap: 12px; }
.change { font-size: 0.75rem; font-weight: 800; padding: 2px 6px; border-radius: 4px; }
.change.up { background: rgba(16, 185, 129, 0.2); color: #10b981; }
.change.down { background: rgba(239, 68, 68, 0.2); color: #ef4444; }
.btn-del { background: none; border: none; color: #ef4444; cursor: pointer; opacity: 0.5; font-size: 1rem; }
.btn-del:hover { opacity: 1; }

.input-row {
    display: flex;
    gap: 8px;
    margin-bottom: 1rem;
}
.input-row input {
    flex: 1;
    background: #1e293b;
    border: 1px solid #334155;
    color: white;
    border-radius: 8px;
    padding: 8px 12px;
    font-size: 0.85rem;
    outline: none;
}
.input-row button { background: #38bdf8; color: #0f172a; border: none; width: 36px; border-radius: 8px; font-weight: 800; cursor: pointer; }

.btn-refresh { background: none; border: none; color: #64748b; font-size: 0.75rem; font-weight: 700; cursor: pointer; text-decoration: underline; }

js:
var stocks = [
    { symbol: 'AAPL', price: 185.92, change: 1.2 },
    { symbol: 'TSLA', price: 238.45, change: -2.4 }
];

function render() {
    var list = api.root.getElementById('stock-list');
    list.innerHTML = '';

    stocks.forEach(function(s, idx) {
        var item = document.createElement('div');
        item.className = 'stock-item';
        item.innerHTML = '<div class="stock-info">' +
                '<span class="symbol">' + s.symbol + '</span>' +
                '<span class="price">$' + s.price.toFixed(2) + '</span>' +
            '</div>' +
            '<div class="stock-right">' +
                '<div class="change ' + (s.change >= 0 ? 'up' : 'down') + '">' +
                    (s.change >= 0 ? 'â–²' : 'â–¼') + ' ' + Math.abs(s.change).toFixed(1) + '%' +
                '</div>' +
                '<button class="btn-del">Ã—</button>' +
            '</div>';

        item.querySelector('.btn-del').onclick = function() { deleteStock(idx); };
        list.appendChild(item);
    });
}

function addStock() {
    var symbol = api.root.getElementById('stock-symbol').value.trim().toUpperCase();
    if (symbol) {
        var price = Math.random() * 500 + 10;
        var change = (Math.random() * 10) - 5;
        stocks.push({ symbol: symbol, price: price, change: change });
        api.root.getElementById('stock-symbol').value = '';
        render();
        api.saveState(stocks);
    }
}

function deleteStock(idx) {
    stocks.splice(idx, 1);
    render();
    api.saveState(stocks);
}

function refreshPrices() {
    stocks = stocks.map(function(s) {
        return {
            symbol: s.symbol,
            price: s.price * (1 + (Math.random() * 0.04 - 0.02)),
            change: (Math.random() * 10) - 5
        };
    });
    render();
    api.saveState(stocks);
}

async function init() {
    var saved = await api.getState();
    if (saved) stocks = saved;

    api.root.getElementById('btn-add').onclick = addStock;
    api.root.getElementById('btn-refresh').onclick = refreshPrices;
    api.root.getElementById('stock-symbol').onkeydown = function(e) { if (e.key === 'Enter') addStock(); };

    render();
}

init();
