css:
:host { display: block; margin: 1.5rem 0; font-family: var(--font-interface); }
.widget-card {
    background: #0f172a;
    color: #f8fafc;
    border-radius: 20px;
    padding: 1.5rem;
    box-shadow: 0 10px 25px rgba(0,0,0,0.3);
    border: 1px solid #1e293b;
    text-align: center;
}
.widget-title { margin: 0 0 1.5rem 0; font-size: 0.9rem; font-weight: 800; color: #38bdf8; text-transform: uppercase; letter-spacing: 1px; }

.input-section {
    background: #1e293b;
    padding: 12px;
    border-radius: 12px;
    margin-bottom: 1.5rem;
    text-align: left;
}
.input-section label { display: block; font-size: 0.65rem; font-weight: 700; color: #38bdf8; margin-bottom: 4px; text-transform: uppercase; }
.input-section input {
    width: 100%;
    background: none;
    border: none;
    color: white;
    font-size: 1.5rem;
    font-weight: 800;
    outline: none;
}

.results-grid { display: grid; gap: 8px; }
.res-item {
    display: flex;
    justify-content: space-between;
    align-items: center;
    background: rgba(255,255,255,0.05);
    padding: 10px 14px;
    border-radius: 10px;
    font-size: 0.85rem;
}
.res-item b { color: #38bdf8; font-family: var(--font-monospace); font-size: 1rem; }

