const { root, saveState, getState } = api;

function convert() {
    const val = root.getElementById('local-time').value;
    if (!val) return;

    const [h, m] = val.split(':').map(Number);
    const baseDate = new Date();
    baseDate.setHours(h, m, 0, 0);

    const localOffset = -new Date().getTimezoneOffset() / 60;
    const utc = baseDate.getTime() - (localOffset * 60 * 60 * 1000);

    const setTime = (id, offset) => {
        const d = new Date(utc + (offset * 60 * 60 * 1000));
        root.getElementById(id).innerText = `${d.getHours().toString().padStart(2, '0')}:${d.getMinutes().toString().padStart(2, '0')}`;
    };

    setTime('time-london', 0);
    setTime('time-ny', -5);
    setTime('time-tokyo', 9);
    setTime('time-paris', 1);

    saveState(val);
}

async function init() {
    const now = new Date();
    const timeStr = `${now.getHours().toString().padStart(2, '0')}:${now.getMinutes().toString().padStart(2, '0')}`;
    root.getElementById('local-time').value = timeStr;

    const saved = await getState();
    if (saved) root.getElementById('local-time').value = saved;

    convert();
}

init();
