:host { display: block; margin: 1.5rem 0; font-family: var(--font-interface); }
.widget-card {
    background: var(--background-secondary);
    border: 1px solid var(--divider-color);
    border-radius: 16px;
    padding: 1.5rem;
    box-shadow: 0 4px 20px rgba(0,0,0,0.06);
}

.widget-header { display: flex; justify-content: space-between; align-items: center; margin-bottom: 1.5rem; }
.widget-title { margin: 0; font-size: 1.1rem; font-weight: 800; color: var(--text-normal); }

.progress-circle {
    width: 40px;
    height: 40px;
    border-radius: 50%;
    background: var(--interactive-accent);
    color: white;
    display: flex;
    align-items: center;
    justify-content: center;
    font-size: 0.75rem;
    font-weight: 800;
}

.input-group {
    display: flex;
    gap: 10px;
    margin-bottom: 1.5rem;
}

.input-group input {
    flex: 1;
    background: var(--background-primary);
    border: 1px solid var(--divider-color);
    color: var(--text-normal);
    border-radius: 10px;
    padding: 10px 14px;
    font-size: 0.9rem;
    outline: none;
}
.input-group input:focus { border-color: var(--interactive-accent); }

.input-group button {
    background: var(--interactive-accent);
    color: white;
    border: none;
    width: 40px;
    height: 40px;
    border-radius: 10px;
    font-size: 1.5rem;
    cursor: pointer;
}

.todo-list {
    display: grid;
    gap: 8px;
    margin-bottom: 1.5rem;
    max-height: 300px;
    overflow-y: auto;
}

.todo-item {
    display: flex;
    align-items: center;
    gap: 12px;
    padding: 12px 16px;
    background: var(--background-primary-alt);
    border-radius: 12px;
    border: 1px solid var(--divider-color);
    transition: all 0.2s;
}
.todo-item.done { opacity: 0.6; }
.todo-item.done .text { text-decoration: line-through; }

.checkbox {
    width: 20px;
    height: 20px;
    border: 2px solid var(--interactive-accent);
    border-radius: 6px;
    cursor: pointer;
    display: flex;
    align-items: center;
    justify-content: center;
}
.checkbox.checked { background: var(--interactive-accent); }
.checkbox.checked::after { content: "‚úì"; color: white; font-size: 12px; font-weight: 800; }

.text { flex: 1; font-size: 0.9rem; font-weight: 600; color: var(--text-normal); }

.btn-del { background: none; border: none; color: var(--text-error); cursor: pointer; font-size: 0.8rem; opacity: 0; transition: opacity 0.2s; }
.todo-item:hover .btn-del { opacity: 0.6; }
.btn-del:hover { opacity: 1 !important; }

.footer {
    display: flex;
    justify-content: space-between;
    align-items: center;
    font-size: 0.75rem;
    color: var(--text-muted);
    font-weight: 600;
}

.btn-clear { background: none; border: none; color: var(--text-muted); cursor: pointer; text-decoration: underline; }
.btn-clear:hover { color: var(--text-error); }

js:
const { root, saveState, getState } = api;

let todos = [];

async function init() {
    const saved = await getState();
    if (saved) todos = saved;
    render();
}

function render() {
    const list = root.getElementById('todo-list');
    list.innerHTML = '';

    let doneCount = 0;
    todos.forEach((todo, idx) => {
        if (todo.done) doneCount++;
        const item = document.createElement('div');
        item.className = `todo-item ${todo.done ? 'done' : ''}`;
        item.innerHTML = `
            <div class="checkbox ${todo.done ? 'checked' : ''}" onclick="toggleTodo(${idx})"></div>
            <span class="text">${todo.text}</span>
            <button class="btn-del" onclick="deleteTodo(${idx})">Delete</button>
        `;
        list.appendChild(item);
    });

    const progress = todos.length > 0 ? Math.round((doneCount / todos.length) * 100) : 0;
    root.getElementById('progress-text').innerText = `${progress}%`;
    root.getElementById('items-left').innerText = `${todos.length - doneCount} items left`;
}

addTodo = async () => {
    const input = root.getElementById('todo-input');
    const text = input.value.trim();
    if (text) {
        todos.push({ text, done: false });
        input.value = '';
        render();
        await saveState(todos);
    }
};

toggleTodo = async (idx) => {
    todos[idx].done = !todos[idx].done;
    render();
    await saveState(todos);
};

deleteTodo = async (idx) => {
    todos.splice(idx, 1);
    render();
    await saveState(todos);
};

clearCompleted = async () => {
    todos = todos.filter(t => !t.done);
    render();
    await saveState(todos);
};

init();
