css:
:host { display: block; margin: 1.5rem 0; font-family: var(--font-interface); }
.widget-card {
    background: var(--background-secondary);
    border: 1px solid var(--divider-color);
    border-radius: 16px;
    padding: 1.5rem;
    box-shadow: 0 4px 20px rgba(0,0,0,0.06);
}

.widget-header { display: flex; justify-content: space-between; align-items: center; margin-bottom: 1.5rem; }
.widget-title { margin: 0; font-size: 1.1rem; font-weight: 800; color: var(--text-normal); }

.progress-circle {
    width: 40px;
    height: 40px;
    border-radius: 50%;
    background: var(--interactive-accent);
    color: white;
    display: flex;
    align-items: center;
    justify-content: center;
    font-size: 0.75rem;
    font-weight: 800;
}

.input-group {
    display: flex;
    gap: 10px;
    margin-bottom: 1.5rem;
}

.input-group input {
    flex: 1;
    background: var(--background-primary);
    border: 1px solid var(--divider-color);
    color: var(--text-normal);
    border-radius: 10px;
    padding: 10px 14px;
    font-size: 0.9rem;
    outline: none;
}
.input-group input:focus { border-color: var(--interactive-accent); }

.input-group button {
    background: var(--interactive-accent);
    color: white;
    border: none;
    width: 40px;
    height: 40px;
    border-radius: 10px;
    font-size: 1.5rem;
    cursor: pointer;
}

.todo-list {
    display: grid;
    gap: 8px;
    margin-bottom: 1.5rem;
    max-height: 300px;
    overflow-y: auto;
}

.todo-item {
    display: flex;
    align-items: center;
    gap: 12px;
    padding: 12px 16px;
    background: var(--background-primary-alt);
    border-radius: 12px;
    border: 1px solid var(--divider-color);
    transition: all 0.2s;
}
.todo-item.done { opacity: 0.6; }
.todo-item.done .text { text-decoration: line-through; }

.checkbox {
    width: 20px;
    height: 20px;
    border: 2px solid var(--interactive-accent);
    border-radius: 6px;
    cursor: pointer;
    display: flex;
    align-items: center;
    justify-content: center;
}
.checkbox.checked { background: var(--interactive-accent); }
.checkbox.checked::after { content: "‚úì"; color: white; font-size: 12px; font-weight: 800; }

.text { flex: 1; font-size: 0.9rem; font-weight: 600; color: var(--text-normal); }

.btn-del { background: none; border: none; color: var(--text-error); cursor: pointer; font-size: 0.8rem; opacity: 0; transition: opacity 0.2s; }
.todo-item:hover .btn-del { opacity: 0.6; }
.btn-del:hover { opacity: 1 !important; }

.footer {
    display: flex;
    justify-content: space-between;
    align-items: center;
    font-size: 0.75rem;
    color: var(--text-muted);
    font-weight: 600;
}

.btn-clear { background: none; border: none; color: var(--text-muted); cursor: pointer; text-decoration: underline; }
.btn-clear:hover { color: var(--text-error); }

js:
var todos = [];

function render() {
    var list = api.root.getElementById('todo-list');
    list.innerHTML = '';

    var doneCount = 0;
    todos.forEach(function(todo, idx) {
        if (todo.done) doneCount++;
        var item = document.createElement('div');
        item.className = 'todo-item' + (todo.done ? ' done' : '');
        item.innerHTML = '<div class="checkbox ' + (todo.done ? 'checked' : '') + '"></div>' +
            '<span class="text">' + todo.text + '</span>' +
            '<button class="btn-del">Delete</button>';

        item.querySelector('.checkbox').onclick = function() { toggleTodo(idx); };
        item.querySelector('.btn-del').onclick = function() { deleteTodo(idx); };

        list.appendChild(item);
    });

    var progress = todos.length > 0 ? Math.round((doneCount / todos.length) * 100) : 0;
    api.root.getElementById('progress-text').innerText = progress + '%';
    api.root.getElementById('items-left').innerText = (todos.length - doneCount) + ' items left';
}

function addTodo() {
    var input = api.root.getElementById('todo-input');
    var text = input.value.trim();
    if (text) {
        todos.push({ text: text, done: false });
        input.value = '';
        render();
        api.saveState(todos);
    }
}

function toggleTodo(idx) {
    todos[idx].done = !todos[idx].done;
    render();
    api.saveState(todos);
}

function deleteTodo(idx) {
    todos.splice(idx, 1);
    render();
    api.saveState(todos);
}

function clearCompleted() {
    todos = todos.filter(function(t) { return !t.done; });
    render();
    api.saveState(todos);
}

async function init() {
    var saved = await api.getState();
    if (saved) todos = saved;

    api.root.getElementById('btn-add').onclick = addTodo;
    api.root.getElementById('btn-clear').onclick = clearCompleted;
    api.root.getElementById('todo-input').onkeydown = function(e) {
        if (e.key === 'Enter') addTodo();
    };

    render();
}

init();
